INSERT INTO `question` VALUES (82, '以下哪些是分布式存储（）', 'SAN', 'NAS', '网盘', '数据库', 2, 'ABC', '\n分布式存储分三类：块存储（如SAN）、文件存储（如NAS）、对象存储（如百度网盘、阿里网盘）。\n知识点\n', '多选题', 39);
