INSERT INTO `question` VALUES (84, '人工智能的目的是让机器能够（  ），以实现某些脑力劳动的机械化。', '具有完全的智能', '和人脑一样考虑问题', '完全代替人', '模拟、延伸和扩展人的智能', 1, 'D', '\n人工智能（Artificial Intelligence），英文缩写为AI。它的定义可以分为两部分，即\"人工\"\"智能\"，它是研究、开发用于模拟、延伸和扩展人的智能的理论、方法、技术及应用系统的一门新的技术科学\n知识点\n', '单选题', 39);
INSERT INTO `question` VALUES (85, '盲人看不到一切物体，他们可以通过辨别人的声音识别人，这是智能的（）方面', '行为能力', '感知能力', '思维能力', '学习能力', 1, 'B', '\n感知能力\n知识点\n', '单选题', 39);
