INSERT INTO `question` VALUES (661, '____调度算法按照进就绪的先后顺序来调度进程,到达得越早，就越先执行， 英语缩写是FCFS.', '', '', '', '', 4, '先来先服务', '先来先服务调度算法按照进就绪的先后顺序来调度进程，到达得越早，就越先执行， 英语缩写是FCFS.\n\n', '填空题', 47);
