INSERT INTO `question` VALUES (663, '在MySQL中执行SQL语句:\nSELECT *, score*0.5+20 as加权成绩\nFROM grade\nWHERE (score*0.5 + 20 < 60)\nORDER BY score*0.5+ 20 ,以下描述是正确的( )', '错误提示:ORDER BY子句中不能出现表达式。', '正确执行,显示grade表中各列值,按照score由低到高排序。', '正确执行,显示grade表中各列值,以及加权成绩列,按照score由低到高排序。', '错误提示:WHERE子句中不能出现表达式。', 1, 'C', '正确执行，显示grade表中各列值以及加权成绩列，按照score由低到高排序。\nORDER BY可以是表达式，也可以是列，也可以是别名\n\n', '单选题', 47);
INSERT INTO `question` VALUES (664, 'x是整型变量，表达式(x=8,x%=x+5) 的值是_____。', '', '', '', '', 4, ' 8', ' 除数，与被除数\n\n', '填空题', 47);
INSERT INTO `question` VALUES (665, '一台交换机具有24个10/ 100Mbps端口和2个1000Mbps端口，如果所有端口都工作在全双工状态，那么交换机总带\n宽应为____Gbps', '', '', '', '', 4, '8.8', '全双工端口带宽的计算方法是:端口数x端口速率x2。\n即24x 100Mbpsx2+2x 1000Mbpsx2\n= 4800Mbps+ 4000Mbps\n= 8800Mbps/1000\n=8.8Gbps\n\n', '填空题', 47);
INSERT INTO `question` VALUES (666, '关键字NULL和___可以给字段自定义约束，NULL值就是没有值或值空缺。', '', '', '', '', 4, 'NOT NULL', '关键字NULL和NOT NULL可以给字段自定义约束，NULL值就是没有值或值空缺。\n\n', '填空题', 47);
INSERT INTO `question` VALUES (667, '若某计算机数据总线的宽度为64位，工作频率为133MHz，每个总线周期传输一次数据，则该计算机的带宽为', '1.064GB/s', '1.064Gb/s', '8.512GB/s', '8.512Gb/s', 1, 'A', '通常单位：MB/s(MBps)\n总线的传输速率=总线的带宽=（总线位宽/8位）*（总线工作频率/总线周期时钟数）\n\n', '单选题', 47);
INSERT INTO `question` VALUES (668, '芯片组一般由北桥芯片和南桥芯片组成，其中南桥芯片连接的器件有', 'BIOS', 'CMOS', '显卡', '硬盘接口', 2, 'ABD', '南桥：PCI总线槽，USB接口，硬盘接口，音频解码器和COMS存储器的连接，\n并借助super I/O芯片提供对鼠标键盘，串行口，并行口等的控制\n\n', '多选题', 47);
INSERT INTO `question` VALUES (669, '矢量图形的获取要经过扫描、分色、取样和量化等几个步骤。', '', '', '', '', 3, '错', '矢量图形，计算机绘制。', '判断题', 47);
INSERT INTO `question` VALUES (670, '计算机中的“木马”程序是一种', '网络病毒', '系统病毒', '硬件病毒', '生物病毒', 1, 'A', '从网络病毒功能区分。可以分为木马病毒和蠕虫病毒\n木马病毒是一种后门程序，它会潜伏在操作系统中\n蠕虫病毒相对来说要先进一点，它的传播途径很广，可以利用操作系统和程序的漏洞主动发起攻击，\n从网络病毒传播途径区分。可以分为漏洞型病毒、邮件型病毒两种。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (671, '在Windows中，硬盘可采用的文件系统格式有\nA．CDFS\nB．FAT\nC．NTFS', 'UDF', '', '', '', 2, 'BC', '。Windows操作系统支持 NTFS, FAT32, and exFAT三种不同文件系统。\nNTFS是目前Windows系统中一种现代文件系统，目前使用最广泛，内置的硬盘大多数都是NTFS格式。\nFAT32是一种相对老旧的文件系统，不能像NTFS格式支持很多现代文件格式的属性，但对于不同系统平台具有良好的兼容性，\n可以在Linux、Mac或Android系统平台上通用。\nexFAT是FAT32文件格式的替代品，很多设备和操作系统都支持该文件系统，但是目前用的不多。\n\n', '多选题', 47);
INSERT INTO `question` VALUES (672, '软件开发过程中,抽取和整理用户需求并建立问题域精确模型的过程叫()', '生存期', '面向对象设计', '面向对象程序设计', '面向对象分析', 1, 'D', '建立问题域->定义期，抽取和整理用户需求说明是需求分析阶段，排除BC。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (673, '“软件工程的概念是为解决软件危机而提出的”这句话的意思是()', '强调软件I程成功解决了软件危机的问题', '说明软件危机的存在总是使软件开发不像传统I程项目那样容易管理', '说明软件工程这门学科的形成是软件发展的需要', '说明软件工程的概念，即:工程的原则、思想、方法可解决当时软件开发和维护存在的问题', 1, 'D', '软件工程.\n(1)软件工程是指采用工程的概念、原理、技术和方法指导软件的开发与维护。\n(2)软件工程的主要思想强调在软件开发过程中需要应用工程化原则。\n(3) 软件工程的核心思想是把软件当作一个工程产品来处理。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (674, '以下不属于Android系统结构层次的是( )', 'Linux操作系统层', '应用程序框架层', '应用程序层', '软件抽象层', 1, 'D', 'Android：应用层，应用框架层，系统运行库，硬件抽象层，linux内核 “也可以优化”\nIOS:触控界面层，媒体层，内核服务层，操作系统内核，“就麻烦你”\n\n', '单选题', 47);
INSERT INTO `question` VALUES (675, '光盘是目前常见的辅助存储器。下列相关叙述中错误的是', '光盘存储器由光盘驱动器和光盘片两部分组成，光盘的基片是铝合金材料', '从接口来看，目前内置DV光驱与主机的接口主要是SATA,外置的主要是USB', 'DVD光盘分为单面单层、单面双层等多种类型，目前最常用的是单面单层的盘片', 'BD光驱采用波长较短的蓝色激光在光盘上进行数据读写，因而提高了光盘存储容量', 1, 'A', '光盘的基片是耐热的有机玻璃。\n硬盘盘片是以坚固耐用的材料为盘基，将磁粉附着在铝合金(新材料也有用玻璃)圆盘片的表面上，表面被加工的相当平滑。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (676, '下列有关ios和Android操作系统的叙述中，正确的是_', '目前iOS主要使用在苹果产品中,例如Pone手机、iPod touch播放器、iPad平 板电脑等', '目前iOS允许用户以任何方式从网上下载并安装运行第3方开发的应用软件', 'Android以Linux内核为基础，早先由Android公司开发, 后被Google公司收购', 'Android大多数模块都是免费开源的，任何厂商都可以不经过授权即可使用', 2, 'ACD', ' 2003年10月，有“Android之父”,在美国加利福尼亚州帕罗奥图共同成立了Android科技公司。\n2005年7月11日，Google以高于$5,000万美元的价钱收购了Android科技公司,\n在Google，由鲁宾领导的团队开发了一个基于Linux内核驱动的移动操作系统的平台，该开发项目便是Android操作系统\n\n', '多选题', 47);
INSERT INTO `question` VALUES (677, '移动互联网是以移动网络作为接入网络的互联网及服务基本要素包括()', '移动终端', '移动网络', '应用服务', '移动平台', 2, 'ABC', '移动互联网是指互联网的技术、平台、商业模式和应用与移动通信技术结合并实践的活动的总称。“移\n动互联网是以移动网络作为接入网络的互联网及服务，包括3个要素:移动终端、移动网络和应用服 务。\n\n\n', '多选题', 47);
INSERT INTO `question` VALUES (678, '在人工智能的研究领域中，有一个领域，主要研究利用计算机对物体、图像、语音、字符等信息模式进行自动识别\n的学科，这门研究分支学科叫____', '', '', '', '', 4, '模式识别', '深度学习是机器学习的分支，是一种以人工神经网络为架构，对资料进行表征学习的算法。\n模式识别就是通过计算机用数学技术方法来研究模式的自动处理和判读。我们把环境与客体统称为“模式”\n\n', '填空题', 47);
INSERT INTO `question` VALUES (679, 'IP地址中如果主机号每一位都是”0 \",称为___。', '', '', '', '', 4, '网络号', ' IP地址中如果主机号每一位都是” 0 \",称为网络地址。如果主机号每一-位都为” 1 \"称为广 播地址。\n\n', '填空题', 47);
INSERT INTO `question` VALUES (680, '鼠标器通常有两个按键，按键的动作会以电信号形式传送给主机，按键操作的作用主要由操作系统决定。', '', '', '', '', 3, '错', '鼠标器通常有两个按键，按键的动作会以电信号形式传送给主机，按键操作的作用主要由所运行的软件\n决定。', '判断题', 47);
INSERT INTO `question` VALUES (681, '在Windows 10上,为了解决不同网络之间切换时需要对网络的相关配置进行重复设置的问题, Windows 10提供\n了()服务,该服务可以自动检测到网强位置的变化,并自动调整网络配置选项以适应当前的网络位置。', '网络位置感知服务', '网络位置共享服务', '网络切换服务', '网络发现', 1, 'A', '在Windows 10上，为了解决不同网络之间切换时需要对网络的相关配置进行重复设置的问题,\nWindows 10提供了(网络位置感知服务)服务,该服务可以自动检测到网强位置的变化，并自动调整\n网络配置选项以适应当前的网络位置。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (682, '在Windows 10内建了大量的_______可以根据实际情况将创建的用户账户添加至不同组中，以便快速为多个用\n户分配所需的权力和权限。', '', '', '', '', 4, '组账户', '`组`账户\n\n', '填空题', 47);
INSERT INTO `question` VALUES (683, '内部转换就是某个状态转换到自身的过程。', '', '', '', '', 3, '错', '内部转换:指的是不导致状态改变的转换。内部转换只有源状态而没有目标状态。表示为”事件名称\n(事件参数) /活动表达式”。', '判断题', 47);
INSERT INTO `question` VALUES (684, '区块链的2.0的典型特征是具有____功能。', '', '', '', '', 4, '智能合约', '区块链的2.0的典型特征是具有智能合约功能。\n\n', '填空题', 47);
INSERT INTO `question` VALUES (685, '.线性表的长度为n。在最坏情况下，比较次数为n-1的算法是( )', '顺序查找', '同时寻找最大项与最小项', '寻找最大项', '有序表的插入', 1, 'C', '顺序查找，包含找不到的情况。寻找最大值，取出表中一个元素，与剩下的n-1个元素比较。\n有序表的插入最坏情况下是插入到表中的最后-个元素的后面位置，则会比较n次。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (686, '字符集及其编码是计算机中表示、存储、处理和交换文本信息的基础，但由于历史原因，目前不同系统或应用中\n使用的字符编码方案并不统一。\n下列编码方案中，所有字符均采用等长编码(编码长度相同)的是_', 'GB2312', 'GB18030', 'UTF-8', 'UTF-16', 1, 'A', ' GB2312编码:所有字符采用2字节编码;\nGB18030: ASCII采用1字节编码,汉字采用2字节编码，其他采用4字节编码;\nUCS有两种标准UTF-8和UTF-16,\nUTF-8采用ASCI码用1个字节编码,拉丁字母，标点符号等采用2字节编码，汉字采用3个字节编码其他极少字符采用4字节编码,\nUCS-16是指ASCII和汉字都采用2字节编码，其他不常用字符采用4字节编码,\n在Windows中文版上现在使用UTF-16,也是Java,NET软件开发环境等默认的编码标准。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (687, '在微软公司的office软件中内置了一种程序设计语言，用户可以使用它来编写程序以扩展功能。该语言是__', 'VBA', 'VBScript', 'Java', 'C#', 1, 'A', 'VBA是Excel，Word，PowerPoint等办公应用程序理解的语言。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (688, '以下是可以用于互联网分配的IP的有( )', '10.10.168.1', '172.32.20.2', '192.168.7.1', '222.222.22.2', 2, 'BD', '私有IP地址\n私有IP地址只用于局域网，不用于互联网，它们是:\n①、10.0.0.0/8 : 10.0.0.0~10.255.255.255.\n②、172.16.0.0~172.31.0.0/16: 172.16.0.0~172.31.255.255.\n③、192.168.0.0~192.168.255.0/24: 192.168.0.0~192.168.255.255\n\n', '多选题', 47);
INSERT INTO `question` VALUES (689, '以下对区块链技术描述优点正确的是()', '去中心化', '透明公开', '完整性', '机密性', 2, 'ABC', '区块链技术具有去中心化、透明公开、数据不可修改等显著优点，CD不是对区块链技术的优点\n1. 它允许匿名的价值转移。\n2. 它提供 24/7 全天候访问您的资金。\n3. 它消除了对中介的需要。\n4.它允许用户完全控制他们的信息。\n5. 它提供了更高水平的透明度。\n6. 它提供了一定程度的过程完整性。\n7. 减少杂乱。\n\n1. 它不是一种 100% 安全的技术。\n2. 不是公认的具有国际价值的技术。\n4. 它可以提供非常缓慢的交易时间。\n5. 访问成本高。\n6. 它要求每个请求都经过单独验证。\n7. 监管地位不确定。\n8. 它需要大量的能量。\n9. 提供多种语言。\n10. 它提供了几乎无限的加密货币\n\n', '多选题', 47);
INSERT INTO `question` VALUES (690, '计算机操作系统一般都支持同时运行多个应用程序,这中功能称为____', '', '', '', '', 4, '多任务处理', '多任务实现的原理是:操作系统将CPU时间划分为许多小时间片,轮流为多个程序服务。多个任务宏\n观上同时运行，微观上轮流运行。多任务处理并不一定需要多核CPU的硬件支撑。\n现在流行的Windows、Linux、 Android、 IOS、 HarmonyOS都支持多 任务处理\n\n', '填空题', 47);
INSERT INTO `question` VALUES (691, '分组交换机的每个端口每发送完-个包才从缓冲区中提取下一-个数据包进行发送。', '', '', '', '', 3, '对', '分组交换机的每个端口每发送完一个包才从缓冲区中提取下一个数据包进行发送。\n分组交换时候，需要排队，可能会有延时。', '判断题', 47);
INSERT INTO `question` VALUES (692, '随着用户需求的增加，IP地址从IPv4版本成绩到IPv6版本，IPv6由256位二进制数组成。', '', '', '', '', 3, '错', '随着用户需求的增加，IP地址从IPv4版本成绩到IPv6版本, IPv6由128位 二进制数组成。', '判断题', 47);
INSERT INTO `question` VALUES (693, '在Windows 10上,若给添加了错误的用户或用户组，可以在该用户或用户组[___\n]列中单击，然后在弹\n出的菜单中选择[]命令以将该用户或用户。', '权限级别', '权限列表', '权限管理', '权限设置', 1, 'A', '在Windows 10上,若给添加了错误的用户或用户组,可以在该用户或用户组[权限级别]列中单击,\n然后在弹出的菜单中选择[删除]命令以将该用户或用户删除。\n\n', '单选题', 47);
INSERT INTO `question` VALUES (694, '在MySQL中，当查询的过滤条件被限定在某个取值范围时，可以使用_____操作符。', '', '', '', '', 4, 'BETWEEN AND', '或许是\n\n', '填空题', 47);
INSERT INTO `question` VALUES (695, '声卡以____ 为核心，其在完成数字音频的编码、解码MIDI声音 合成及音频编辑操作中起着重要的作用。', '', '', '', '', 4, ' DSP', '声卡以(DSP)为核心，其在完成数字音频的编码、解码MIDI声音 合成及音频编辑操作中起着重要的作\n用。DSP是即数字信号处理器，-种专用的微处理器。\n\n', '填空题', 47);
INSERT INTO `question` VALUES (696, '从系统功能的角度看，计算机网络主要由资源子网和通信子网两部分组成。其中通信子网主\n要包括连网的计算机、终端、外部设备、网络协议及网络软件等。', '', '', '', '', 3, '错', '网络软件一般是指系统的网络操作系统（进程，端口号）属于资源子网', '判断题', 47);
INSERT INTO `question` VALUES (697, '下列关于操作系统的叙述，错误的是', '能为用户提供友善的人机界面', '能为用户提供一个容量比实际内存大得多的虚拟内存', '其设备管理功能主要负责计算机系统中所有外围设备的管理', '其存储管理功能主要负责存储器空间的分配与回收、文件的创建与\n正确答案', 1, 'D', '设备管理：对除`cpu,内存`外的`所有`的I/O设备进行管理。\n文件管理：主要负责存储器空间的分配与回收、文件的创建与删除\n存储管理：主要对内存管理', '单选题', 47);
INSERT INTO `question` VALUES (698, '下列关于CPU的叙述.正确的有', 'CPU是运算器与控制器的总称', 'CPU中包含有若干个寄存器', 'CPU可直接访问外存中的数据', 'CPU承担了系统软件与应用软件的运行任务', 2, 'ABD', '定义不同：有时候运算器，控制器中包含包含寄存器，有时候寄存器又独立与运算器，控制器\n\n', '多选题', 48);
INSERT INTO `question` VALUES (699, '在图像的数字化过程中，把取样点每个分量的亮度用数字量来表示的过程称为______', '', '', '', '', 4, '量化', '分色：将彩色图像分解成RGB三种基色\n量化：取样点每个分量的亮度进行A/D转化，用数字整型来表示\n\n', '填空题', 48);
INSERT INTO `question` VALUES (700, '应用程序Telnet采用的端口号默认为( )', '20', '21', '23', '25', 1, 'C', 'FTP数据20，控制连接21; ss:22;Telnet:23;SMTP:25;POP:110;HTTP:80;\n\n', '单选题', 48);
INSERT INTO `question` VALUES (701, '资源的全部分配可以破坏( ) 条件。', '互斥', '请求和保持', '不剥夺', '环路等待', 1, 'B', '请求和保持条件是指进程已经保持了至少一个资源，但又\n提出了新的资源请求，而该资源又已被其它进程占有的情况，而资源的全部分配可以\n破坏请求和保持条件。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (702, '关于TCP可靠数据传输服务的论述，正确的是( ) 。', '只有当超时事件发生，TCP才会执行重传', '每次TCP重传时，都重新为定时器设置一个固定时间间隔', 'TCP接收方不需要对乱序到达的分组进行确认', '一旦收到3个冗余ACK，TCP就执行快速重传', 1, 'D', '重传是可靠性的保证，是发送端感知到网络出现丢包，主动发起重传。\n快速重传:当接收方收到的数据包是不正常的序列号，那么接收方会重\n复把应该收到的那一条ACK重复发送，这个时候，如果发送方收到连续3条的同-一\n个序列号的ACK,那么就会启动快速重传机制，不需要等到timeout,把这个ACK对\n应的发送包重新发送一一次。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (703, '假设某计算机浮点数表示的规定如下:浮点数尾数基值为4，尾数数值部分长\n为8位二进制位，可表示规格化正尾数的个数是( ) 。', '192', '448', '256', '128', 1, 'A', '尾数的个数由尾数数值的位数确定，2^8=256。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (704, '面向用户的文件组织机构属于( ) 。', '虚拟结构', '实际结构', '逻辑结构', '物理结构', 1, 'C', '文件的逻辑结构是从用户观点出发所观察到的文件组织形式\n而文件的物理结构是指文件在外存上的存储组织形式，\n\n', '单选题', 48);
INSERT INTO `question` VALUES (705, 'IP协议可以( )', '实现流量控制', '进行拥塞控制', '保证可靠传输', '充分利用网络资源', 1, 'D', 'IP协议是一种不可靠的传输，不能进行流量控制，拥塞控制等，只能尽最大努力交付。\nTCP可靠传输，流量控制，拥塞控制\n\n', '单选题', 48);
INSERT INTO `question` VALUES (706, '若当前进程因时间片用完而让出处理机时，该进程应转变为()状态。', '就绪', '等待', '运行', '完成', 1, 'A', '三态模型：进程一般有3种基本状态：运行、就绪和阻塞。\n（1）就绪：当一个进程获得了除处理机以外的一切所需资源，一旦得到处理机即可运行，则称此进程处于就绪状态。就绪进程可以按多个优先级来划分队列。例如，当一个进程由于时间片用完而进入就绪状态时，排入低优先级队列；当进程由I／O操作完成而进入就绪状态时，排入高优先级队列。\n（2）运行：当一个进程在处理机上运行时，则称该进程处于运行状态。处于此状态的进程的数目小于等于处理器的数目，对于单处理机系统，处于运行状态的进程只有一个。在没有其他进程可以执行时（如所有进程都在阻塞状态），通常会自动执行系统的空闲进程。\n（3）阻塞：也称为等待或睡眠状态，一个进程正在等待某一事件发生（例如请求I/O而等待I/O完成等）而暂时停止运行，这时即使把处理机分配给进程也无法运行，故称该进程处于阻塞状态。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (707, '易理解性是衡量算法是否易调、易测和易维护的重要标准。', '', '', '', '', 3, '错', '算法不需要维护', '判断题', 48);
INSERT INTO `question` VALUES (708, '在Windows中，管理员用户名Administrator 是无法修改的。', '', '', '', '', 3, '错', '每一台计算机至少需要一个帐户拥有administrator（管理员）权限，但不一定非用“Administrator”这个名称不可。', '判断题', 48);
INSERT INTO `question` VALUES (709, '下列关于丰富格式文本的叙述，错误的是', 'Word、WPS文档都是丰富格式文本', 'HTML、PDF文档采用统一的格式控制符', '丰富格式文本通常可以插入图片、表格和超链接', '丰富格式文本可以讲行排版并保留排版信息', 1, 'B', '丰富格式文本包括线性文本和超文本。\nHTML、PDF：PDF与HTML 可移植文档格式（通常称为PDF）是Adobe创建的一种格式，用于传输文档而不进行更改\nHTML和PDF文件之间的主要区别在于输出。\nPDF文件使用的字体也在文档本身内定义，只是为了确保输出的准确性。\nHTML文件通常依赖于系统的字体，这为HTML文件的格式化引入了另一组问题。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (710, '在Windows中，下列文件名正确有的', 'a+b. 123', 'a-b. exe', 'a*b. txt', 'a/b. doc', 2, 'AB', '文件名不能包含下列任何字符:/:?*<>|\n\n', '多选题', 48);
INSERT INTO `question` VALUES (711, '攻树T的度为4，其中度为1，2，3和4的结点个数分别为4，2，1，1，则T\n中的叶子数为( )。', '5', '6', '7', '8', 1, 'D', '0*n0+1*n1+2*n2+3*n3+4*n4=n-1  :条数相等，n为总结点数，n1，n2……分别为度为1，度为2……节点数\n0*n0+1*n1+2*n2+3*n3+4*n4+1=n0+n1+n2+n3+n4; 节点数相等\n带值==> n0=8\n\n', '单选题', 48);
INSERT INTO `question` VALUES (712, '网卡的主要功能不包括()。', '将计算机连接到通信介质上', '进行电信号匹配', '实现数据传输', '网络互连', 1, 'D', '网络互连是路由器，路由器没有网卡了，不过有MAC地址，MAC地址也叫物理地址，网卡、集线器、路由器等都有，并不只有网卡才有。路由器有分配、管理IP的功能。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (713, '反映计算机基本功能的是()。', '操作系统', '系统软件', '指令系统', '数据库系统', 1, 'C', '指令系统，计算机中各种指令的集合，它反映了计算机硬\n件具备的基本功能。系统软件扩展了裸机的功能；系统软件，数据库系统，又是在系统软件的基础上实现各自功能的软件。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (714, '程序段\nfor (i=n-1;i>=1;i--)\n    for (j=0;j<:j++)\n        if (A[j>A[j+1]){\n            k=A[j];\n            A[j]=A[j+1];\n            A[j+1]=k;\n            }\n其中n为正整数，则最后一行的语句频度在最坏情况下是( ) 。', 'O (n)', 'O (nlog2n)', 'O (n3)', 'O (n2)', 1, 'D', '程序段中的基本操作语句是if语句中的A[j]与A[j+1]交换\n的语句，它执行次数最坏时为n2，即时间复杂度为0(n2)。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (715, '进程调度的关键问题是选择合理的()，并恰当地进行代码转换。', '时间片间隔', '调度算法', 'CPU速度', '内存空间', 1, 'B', '调度算法是指根据系统的资源分配策略所规定的资源分配算法\n\n', '单选题', 48);
INSERT INTO `question` VALUES (716, '虚拟存储管理系统的基础是程序的()理论。', '虚拟性', '动态性', '局部性', '全局性', 1, 'C', '虚拟存储管理系统的基础是程序的局部性理论。也能理解，LRU,FIFO跟Cache\n\n', '单选题', 48);
INSERT INTO `question` VALUES (717, '隐含寻址的指令中没有给出操作数的地址，该操作数来自()。', '立即数和堆栈', '暂存器', '堆栈的栈顶和次栈顶', '累加器或堆栈', 1, 'D', '隐含寻址是指指令字中不明显地给出操作数的地址，其操作数的地址隐含在操作码或某个寄存器，\n如一地址格式的加法指令只给出一个操作数的地址,另一个操作数隐含在累加器ACC中。\n另外，以堆栈寻址方式工作的指令，一般都不是明显地给出操作数的地址，而是隐含着操作数的地址，这个地址就是栈顶。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (718, '当你感觉到你的Win7运行速度明显减慢,当你打开任务管理器后发现CPU的\n使用率达到了100%，你最有可能受到了以下哪一种攻击( ) 。', '特洛伊木马', '拒接服务', '更改报文流', '流量分析', 1, 'B', '拒绝服务攻击即\nDoS，Denial of Service的简称，\n攻击者想办法让目标机器停止提供服务，是骇客常用的攻击手段之一。\n其实对网络带宽进行的消耗性攻击只是拒绝服务攻击的一小部分，\n只要能够对目标造成麻烦，使某些服务被暂停甚至主机死机，\n都属于拒绝服务攻击。\n流量分析:除了基本的网站流量统计数据分析之外，还包括用户行为分析、网站流量与网络营销策略关联分析,网络营销策略的问题诊断,以及对网络营销策略的相关建议等。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (719, '基带系统是使用( ) 进行传输的。', '模拟信号', '多信道模拟信号', '数字信号', '多路数字信号', 1, 'C', '基带系统是使用数字信号进行传输的，频带系统是使用模拟信号进行传输的。\n未对载波调制的待传信号称为基带信号\n频带传输是信号经调制后传输到终端后经再解调的传输方式\n\n', '单选题', 48);
INSERT INTO `question` VALUES (720, '以 下关于IPv4数据报结构的描述中，错误的是', 'IPv4数据报的首部长度是可变的', 'IPv4数据报首部中的协议字段表示IP的版本，值为4表示IPv4', 'IPv4数据报首部中的首部长度字段以4B为单位，总长度字段以字节为单位', 'IPv4数据报首部中的生存时间字段的值表示一个IPv4数据报可以经过路由器的数量', 1, 'B', '版本:IPv4,协议:协议字段用来标识此IP数据报在传输层所采用的协议类型（如TCP、UDP或ICMP等等）\n\n', '单选题', 48);
INSERT INTO `question` VALUES (721, '在Windows中，卸载磁盘上不在需要的软件，可以直接软件的安装目录。', '', '', '', '', 3, '错', '有得软件可以直接删除，这种软件叫绿色安装\n有的不能直接删除;表述不完整，也许', '判断题', 48);
INSERT INTO `question` VALUES (722, '下列网络信息安全措施中，通常采用数字签名技术的有', '真实性鉴别', '数据加密', '防止否认', '审计管理', 2, 'AC', '数字签名技术采用了数据加密技术\n', '多选题', 48);
INSERT INTO `question` VALUES (723, '在题87图的空白框中填写正确的名称，完善高级语言编译过程示意图。\n源程序 --编译-->____ --连接--> 可执行程序', '', '', '', '', 4, ' 目标程序', 'C语言代码经过编译以后，并没有生成最终的可执行文件（.exe 文件），而是生成了一种叫做目标文件（Object File）的中间文件（或者说临时文件）。\n目标文件也是二进制形式的，它和可执行文件的格式是一样的。对于 Visual C++，目标文件的后缀是.obj；对于 GCC，目标文件的后缀是.o。\n目标文件经过链接（Link）以后才能变成可执行文件。既然目标文件和可执行文件的格式是一样的，为什么还要再链接一次呢，直接作为可执行文件不行吗？\n不行的！因为编译只是将我们自己写的代码变成了二进制形式，它还需要和系统组件（比如标准库、动态链接库等）结合起来，这些组件都是程序运行所必须的。\n链接（Link）其实就是一个“打包”的过程，它将所有二进制形式的目标文件和系统组件组合成一个可执行文件。\n完成链接的过程也需要一个特殊的软件，叫做链接器（Linker）。\n\n', '填空题', 48);
INSERT INTO `question` VALUES (724, '系统测试包括以下测试中的_______三 部分。\n①过程测试②窗体测试③模块测试④系统测试⑤验收测试', '①②③', '②③④', '②③⑤', '③④⑤', 1, 'D', '这里说的系统测试指的应该是软件测试，模块设置~单元测试。还差一个集成测试就凑够是个了。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (725, '为配合疫情防控的需要，高清视频会议系统、医疗机构在线远程高清诊断系统、居\n家隔离远程高清探头已经大量部署，该类系统设备录制的视频编码格式--般为', 'MPEG- 1', 'MPEG-2', 'MPEG-4 AVC', 'MPEG-4 ASP', 1, 'C', ' MPEG-4 AVC是MPEG-4的第10部分，而我们平时所说的MPEG-4是指MPEG-4的第2部分，\n压缩生成的是MPEG-4 ASP(Advanced Simple Profile，高级简单类)格式的视频文件。\n也就是说，MPEG-4通常是MPEG-4 ASP的简称。\nMPEG-4 AVC和H.264和是同一个概念，是分属于不同组织的不同名称，\nMPEG-4 AVC的压缩算法极为复杂，但压缩率极高，同等画质下，文件体积比MPEG-2小1倍以上，\nMPEG-4 AVC的应用领域之一是数字电视，包括卫星通信广播、地面数字电视、IPTV（网络电视）等。\n在CE（消费电子）领域，MPEG-4 AVC是下一代高清影碟机HD-DVD和BR-DVD（Blu-Ray DVD，蓝光DVD）的压缩格式之一。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (726, '信息系统实施中，工作量最大且最细微的工作是', '网络系统的安装、联通和调试', '计算机系统的安装、调试和试运行', '数据库的设计和应用软件的编制和调试', '新、旧系统的转换、排错、修改和模拟运行', 1, 'C', '本题考点是系统实施的任务。信息系统实施阶段，工作量最大、最困难的\n是主题数据库的实现和应用程序的编制。因为数据库的设计与实施在很大程度上影响系\n统的质量，而程序的编制必须经过编码、测试、排错3个层次。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (727, '大数据时代，数据使用的关键是', '数据收集', '数据再利用', '数据存储', '数据分析', 1, 'B', '大数据时代数据使用的关键是数据再利用。记就完事了\n\n', '单选题', 48);
INSERT INTO `question` VALUES (728, '以下哪项与可视化技术没有关系', '地图导航系统', '股票k线', '百度贴吧帖子', '停车场监控管理', 1, 'C', '\n停车场车位感应设备和进出门闸设备通过设备集成连接到ROMA Connect，实时上报车位状态信息和车辆出入信息。\n设备上报的数据经规则引擎转发到消息集成的Topic上存储。\n通过数据集成任务把Topic中的停车场数据实时写入停车场管理系统的数据库中，用于车位状态监测和停车计费，并可在IOC大屏上显示。\n通过数据API把数据库中的车位状态数据开放给第三方应用，供车主、城市管理者了解停车场车位情况。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (729, '下列关于数字图像的叙述中，错误的是', '一幅彩色图像数据量计算公式为:图像数据量(单位为字节) =图像水平分辨率X\n图像垂直分辨率', '黑白图像或灰度图像的每个取样点只有一个亮度值', '对模拟图像进行量化的过程也就是对取样点的每个分量进行D/A转换:', '取样图像在计算机中用矩阵来表示，矩阵行数称为水平分辨率，矩阵列数称为图\n像的垂直分辨率', 2, 'ABD', '若采样结果每行（即横向）像素为M个，每列（即纵向）像素为N个，则整幅图像的大小为MxN个像素\n不是分辨率是像素\n\n', '多选题', 48);
