INSERT INTO `question` VALUES (733, '下列关于打印机的叙述，正确的有___', '9针打印机的打印头由9根钢针组成', '碳粉/硒鼓是激光打印机的主要耗材', '喷墨打印机与激光打印机的打印速度均用每分钟打印的页数来度量', '目前激光打印机均为黑白打印机，而喷墨打印机均为彩色打印机', 2, 'ABC', '记一下这个-->喷墨打印机与激光打印机的打印速度均用每分钟打印的页数来度量\n\n', '多选题', 48);
INSERT INTO `question` VALUES (734, '下列关于超文本中文本块的叙述，正确的有', '文本块不能分布在不同的Web服务器中B.文本块之间的关系是线性的', '文本块之间由指针进行链接', '文本块中的数据可以是文字、图形、图像、声音和视频', '', 2, 'CD', '超链是有向的，起点位置称为链源，节点可以是文字、图形、图像、声音和视频\nHTML 超文本标记语言\n\n', '多选题', 48);
INSERT INTO `question` VALUES (735, '数码相机能直接将图像信息以数字形式输入计算机进行处理，目前数码相机中将光信号转换\n为电信号的器件主要有CMOS和____', '', '', '', '', 4, ' CCD', 'CCD是电荷耦合器件（charge coupled device）的简称，它能够将光线变为电荷并将电荷存储及转移，也可将存储之电荷取出使电压发生变化，因此是理想的CCD相机元件，以其构成的CCD相机具有体积小、重量轻、不受磁场影响、具有抗震动和撞击之特性而被广泛应用。\nCMOS 是DRAM 敏感  易受光线影响\n\n', '填空题', 48);
INSERT INTO `question` VALUES (736, '在IPv4地址的分类编址阶段,每个B类网包含的IP地址数量为', '2^8', '2^8 -2', '2^16', '2^16 -2', 1, 'C', '包含的IP地址数量,不是可分配的主机数。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (737, '信息技术是用来扩展人们信息器官功能、协助人们更有效的进行信息处理的一类技术， 它引\n发了第三次工业革命。', '', '', '', '', 3, '对', '第三次科技革命又名第三次工业革命、信息技术革命、数字化革命、信息革命、科技革命，\n二战后，自动化的计算机提升了生产力，核技术提供了超过石油的发电效率。\n它是人类历史上规模最大、影响最深远的科技革命，至今仍未结束。', '判断题', 48);
INSERT INTO `question` VALUES (738, '用微信软件传送文字、语音、图片和视频等信息主要体现的计算机网络功能是_', '分布式信息处理', '数据通信', '提供系统可靠性', '资源共享', 1, 'B', '所谓共享资源就是指网络中的用户都能够部分或全部地享受这些资源\n数据通信也是计算机网络最基本的功能。它用于快速传送计算机与终端、计算机与计算机之间的各种信息，包括文字信息、图片资料等等\n\n', '单选题', 48);
INSERT INTO `question` VALUES (739, 'USB接口有效传输速率已达到35Gbps,该接口的版本是______', '', '', '', '', 4, ' USB4.0 ', '\nUSB1.1 : 1.5MB/S\nUSB2.0 : 60MB/s,\nUSB3.0 : 625MB/S 5Gbps\nUSB3.1 : 1250MB/S 10Gbps\nUSB3.2 : 2500MB/S 20Gbps\nUSB4.0 : 5000MB/S 40Gbps\n\n', '填空题', 48);
INSERT INTO `question` VALUES (740, '移动通信指的是处于移动状态的对象之间的通信,它由移动台、_______、移动电话交换中心.\n等组成。', '', '', '', '', 4, ' 基站', '现代移动通信技术主要可以分为低频、中频、高频、甚高频和特高频几个频段，在这几个频段之中，技术人员可以利用移动台技术、基站技术、移动交换技术，对移动通信网络内的终端设备进行连接，满足人们的移动通信需求。\n\n', '填空题', 48);
INSERT INTO `question` VALUES (741, '电子商务中信息密度的增加是由于Internet和Web使得价格更加透明化，成本更加透\n明化，价格歧视减少。', '', '', '', '', 3, '错', '信息密度：通过电子商务或Internet可以获取的有关特定产品的信息量。\n几乎所有具有相同产品的电子商务都以相同的描述出售它们。\n 但是，有些人则对他们提供的信息表示出更多的兴趣，以提供更多，更高质量和更实用的信息。\n  这样可以更好地与用户建立联系，并做出更适当的决定。\n价格更加透明化，成本更加透明化，价格歧视减少。明显在吹牛逼', '判断题', 48);
INSERT INTO `question` VALUES (742, '凯撒密码是-种古老加密方法，其基本思想是:把明文中的所有字母在字母表中向\n后(或者向前)按照一一个固定位数进行偏移再替换成密文。如偏移量取3，所有字母\nA被替换成D，B替换成E，以此类推，X替换成A，Y替换成B。采用该算法加密，偏移\n量取4，然后把替换后字符的ASCII码用十六进制表示即为加密结果，则字符“A”对\n应的加密结果是', 'D', 'E', '45', '69', 1, 'C', '用十六进制表示\n\n', '单选题', 48);
INSERT INTO `question` VALUES (743, '从信息处理的深度看，卜 列叙述中错误的是', '信息处理系统分为管理业务系统、辅助技术系统和办公信息系统', '管理业务系统有面向操作层和面向管理层二类', 'CAD、CAM属于辅助技术系统', '信息检索系统属于信息分析系统', 1, 'D', '计算机辅助技术(computer aided technology)是 以计算机为工具，辅助人在特定应用领域内完成任务的理论、方法和技术。\n它包括计算机辅助设计(CAD)、计算机辅助制造(CAM)、计算机辅助教学(CAI)、计算机辅助质量控制(CAQ)及计算机辅助绘图等。\n“辅助”是强调人的主导作用\n所以可以说任何具有信息存储与信息检索功能的系统都可以称为信息检索系统\n信息检索系统可以理解为一种可以向用户提供信息检索服务的系统\n\n', '单选题', 48);
INSERT INTO `question` VALUES (744, '以下说明中的术语分别来自关系模型、程序员和用户，其中具有正确对应关系的是', '关系模式、文件和表', '二维表、记录和行', '元组、记录和行', '属性、记录和列', 1, 'C', '同一事物的不同人的描(关系模型、程序员和用户)\n\n', '单选题', 48);
INSERT INTO `question` VALUES (745, '大数据产品优势中，下面对于数据能力采集处理快描述错误的是', '每毫秒10万组词逻辑匹配', '每秒钟高速流式大数据处理10万篇文章', '每分钟2200万贴吧全扫描', '每日去重原创信息采集近1.5亿', 1, 'A', '逻辑匹配可能不是数据能力采集\n\n', '单选题', 48);
INSERT INTO `question` VALUES (746, '数码相机一般使用闪炼仔储器制作仔储下，卜列可以作为相机仔储下的是', 'TF (Micro SD)卡', 'CF卡', 'DRAM', 'SD卡', 2, 'ABD', '  TF Card是TransFlash Card的缩写;\nMicro SD卡是一种可移动的闪存卡\nCF卡（Compact Flash）最初是一种用于便携式电子设备的数据存储设备。\n\n', '多选题', 48);
INSERT INTO `question` VALUES (747, '下列关于脏数据的说法中，正确的是', '格式不规范', '编码不统一', '意义不明确', '与实际业务关系不大', 2, 'ABCD', '从广义上看，脏数据是指没有进行过数据预处理而直接接收到的、处于原始状态的数据；从狭义上看，是不符合研究要求，以及不能够对其直接进行相应的数据分析。\n脏数据依据不同的分析目的有不同的定义，如在常见的数据挖掘工作中，脏数据是指不完整、含噪声、不一致的数据；而在问卷分析中，脏数据则是指不符合问卷要求的数据。\n\n', '多选题', 48);
INSERT INTO `question` VALUES (748, '下列关于FTP协议的叙述中，错误的是（）', '数据连接在每次数据传输完毕后就关闭', '控制连接在整个会话期间保持打开状态', '服务器与客户端的TCP 20端口建立数据连接', '客户端与服务器的TCP 21端口建立控制连接', 1, 'C', '\nFTP客户和服务器之间要建立“控制连接”和“数据连接”这两个并行的TCP连接。控制连接在\n整个会话期间都保持打开状态，而数据连接在每次文件传输时才建立，传输结束就关闭。\n默认情况下，FTP使用TCP 21端口进行控制连接, TCP 20端口进行数据连接。\n但是，是否使用TCP 20端口建立数据连接与传输模式有关，主动方式使用TCP 20端口，被动方\n式由服务器和客户端自行协商决定。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (749, '现在大多数操作系统都有网络通信功能，Linu', '', '', '', '', 3, '错', '常见的网络操作系统有:\nWindows 2000 Server\nWindows 2003 Server\nUnix\nLinux\n所以winXP 不是网络操作系统 是本地操作系统 XP是基与NT的 XP的正式名应该是windows NT 5.1\n一般说的NT是指windows NT 4.0\nWindows 2000是NT 5.0\nNT的意思是new techology即新技术的意思不是所谓的网络（network)的简写', '判断题', 48);
INSERT INTO `question` VALUES (750, 'PAL制式的彩色电视信号传输时采用YUV颜色模型，其中U是', '饱和度.', '对比度', '亮度', '色度', 1, 'D', 'CMYK(青cyan 品红magenta 黄yellow 黑black) 彩色打印机\nHSB(色度hues 饱和度saturation 亮度brightness)  用户界面\nYUV(亮度 UV是不同的色度) 彩色电视\n\n', '单选题', 48);
INSERT INTO `question` VALUES (751, '下列关于操作系统任务的叙述，正确的有', '任务是已经启动执行的一个应用程序', '结束任务就是从外部存储设备上该任务所对应的程序', '一个程序可以产生多个任务', '操作系统只把用户输入的信息传送到活动窗口所对应的前台任务中', 2, 'ACD', '记一下这个-->操作系统只把用户输入的信息传送到活动窗口所对应的前台任务中\n\n', '多选题', 48);
INSERT INTO `question` VALUES (752, '下列选项中属于数据库管理系统软件的有I', 'MySQL', 'ORACLE', 'VB', 'VFP', 2, 'ABD', '常见的关系型数据库：VFP~(Visual FoxPro)、SQL Server、MySQL、Access\n非关系型（NoSql）Redis Hbase MongodDB\n\n', '多选题', 48);
INSERT INTO `question` VALUES (753, '在Windows中，控制面板是硬盘上的一个文件夹。', '', '', '', '', 3, '错', '控制面板不是硬盘上的文件夹，是很多基本的设定功能的一个集合，是一个程序 control.exe。\n桌面倒确实是文件夹，英文名叫desktop。', '判断题', 48);
INSERT INTO `question` VALUES (754, '分组交换机本身也是一种专用计算机，它的基本工作模式是_____', '', '', '', '', 4, ' 存储转发', '分组交换网络采用“存储—转发”方式，\n\n', '填空题', 48);
INSERT INTO `question` VALUES (755, '一次打印输出结束后，进程获得请求的打印机资源，将使进程状态从( )。', '运行态变为就绪态', '运行态变为等待态', '就绪态变为运行态', '等待态变为就绪态', 1, 'D', '运行中的进程有3种状态：运行状态、就绪状态和等待状态。\n（1）运行状态是指进程已获得CPU，并且在CPU中执行的状态。\n（2）就绪状态是指进程已具备运行条件，但由于没有获得CPU而不能运行所处的状态，\n一旦CPU分配给它，就可以运行。\n（3）等待状态是指进程因等待某种事件发生而暂时不能运行的状态。\n打印输出结束后，进程得到请求的打印机资源，即已具备运行条件，只要再获得CPU，\n便可立即投入执行，故是由等待状态变为就绪状态。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (756, '微程序控制器中的控制存储器用来存放()。', '机器指令和数据', '微程序和数据', '机器指令和微程序', '微程序', 1, 'D', '微程序控制器中的控制存储器用来存放实现整个指令系统的所有微程序。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (757, 'CPU响应中断时，最先完成的两个步骤是( ) 和保护现场信息。', '开中断', '恢复现场', '关中断', '不可屏蔽中断', 1, 'C', 'PU响应中断时，需要先关中断，保证不受其它中断请求的影响。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (758, 'ISDN网络语音和数据传输的全双工数据通道(B通道)速率为()', '1 6kbps', '64kbps', '128kbps', '256kbps', 1, 'C', 'ISDN(综合数据网，窄带)基本速率接口包括两个能独立工作的B信道（64kbps）\n和一个D信道（16kbps），\n其中B信道一般用来传输话音、数据和图像，\nD信道用来传输信令或分组信息。\n此题中，该信道为全双工的B数据通道，\n因此，其速率为64kbps*2=128kbps\n\n', '单选题', 48);
INSERT INTO `question` VALUES (759, '磁盘存储器的记录方式采用( ) 。', '归零制(RZ)', '不归零制(NRZ)', '改进调频制(MFM )', '调相制( PM)', 1, 'C', '磁盘存储器的记录方式采用改进调频制（MFM）\n计算机组成原理上说调相制一般用于磁带存储器上，\n而书上对归零制强调已经淘汰，对不归零的没有任何应用的解释\n对未改进的调频制说应用在硬盘和软盘上很广，\n我就觉得改进的应该也差不多吧 应该是C\n\n', '单选题', 48);
INSERT INTO `question` VALUES (760, '若有18个元素的有序表存放在- -维数组A[19]中，第一个元素放A[1]中，现\n进行二分查找，则查找A [3]的比较序列的下标依次为( ) 。', '9，5，3', '9，5，2，3', '1，2，3', '9，4, 2，3', 1, 'D', '二分查找的基本思想是将n个元素分成大致相等的两部分，\n取中间位置的结点值与关键字做比较，（中间点的判断：left+(right-left),left和right均为下标 )\n如果相等，\n则查找成功；\n如果关键字的值小于中间结点，\n则只要在数组的左半部分继续搜索，（不包括之前的中间点）\n重复与中间值进行比较，\n直至查找成\n功或失败；\n如果关键字大于中间值，（不包括之前的中间点）\n则只要在数组的右半部搜索即可。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (761, '设指针变量p指向单链表中结点A,若单链表中结点A,则需要修改指针\n的操作序列为( ) 。', 'q=p->next; p->data=q->data; p->next=q->next; free (q) ;', 'q=p->next; p->data=q->data; free (q) ;', 'q=p->next; p->next=q->next; free (q) ;', 'q=p->next; q->data=p->data; p->next=q->next; free (q) ;', 1, 'A', '实际删除的是A的后继结点B，（A的内存单元不动，删除B的内存单元）\n先将A的后继结点B复制到A中，（A的内存单元数据域存放B.data）\n且A的后继改为原来B的后继，A的内存单元下一个指针域存放B.next）\n再将B删除。作用等同于删除A。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (762, '在ISO的OSI安全体系结构中，()可以提供抗抵赖安全服务。', '加密解密', '路由控制', '访问控制', '数字签名', 1, 'D', '抗抵赖：数字签名\n\n', '单选题', 48);
INSERT INTO `question` VALUES (763, '虹膜识别技术是人体生物识别技术的--种，自动虹膜识别系统包括虹膜图像获，\n取装置和虹膜识别算法，分别对应( )。', '图形获取和特征辨别', '图像扫描和形式辨别', '图形扫描和模糊匹配', '图像获取和模式匹配', 1, 'D', '虹膜识别技术步骤：\n第一步使用特定的摄像器材对人的整个眼部进行拍摄；\n第二步对获取到的虹膜图像进行处理，使其满足提取虹膜特征的需求；\n第三步采用特定的算法从虹膜图像中提取出虹膜识别所需的特征，并对其进行编码；\n第四步将得到的`特征编码`与`数据库`中的虹膜图像特征编码逐一`匹配`，判断是否为相同虹膜，\n从而达到身份识别的目的。\n\n', '单选题', 48);
INSERT INTO `question` VALUES (764, '下列说法正确的有()。', '所谓数据的逻辑结构是指数据元素之间的逻辑关系', '同一数据逻辑结构中的所有数据元素都具有相同的特性是指数据元素所包含的\n数据项的个数相等', '数据的逻辑结构与数据元素本身的内容和形式无关', '数据结构是指相互之间存在一种或多种关系的数据元素的全体', 2, 'AC', '\n数据结构是指相互之间存在一种或多种“特定”关系的数据元素的全体。F少两字\n数据结构：是指由某一数据对象中所有数据元素成员之间的关系组成的集合\n算了记住就行\n\n', '多选题', 48);
