INSERT INTO `question` VALUES (769, '某计算机字长16位，存储器容量64KB,若按字节编址，那么它的寻址范围是\n()。', '64K', '32K', '64KB', '32KB', 1, 'A', '单位\n\n', '单选题', 48);
INSERT INTO `question` VALUES (770, '对记录式文件，操作系统为用户存取文件信息的最小单位是( ) 。', '字符', '数据项', '记录', '文件', 1, 'C', '文件系统把数据分为数据项、记录和文件三级。\n数据项是最低级的数据组织形式，\n而记录是一组相关数据项的集合，每个记录可以独立存取，\n因此为用户存取文件信息的最小单位。\n\n', '单选题', 48);
