INSERT INTO `question` VALUES (800, '物联网体系架构中,应用层相当于人的', '大脑', '皮肤', '社会分工', '神经中枢', 1, 'C', '应用层相当于物联网的“社会分工”，即与行业需求结合，实现广泛智能化。\n应用层是物联网与行业专业技术的深度融合，与行业需求结合，实现行业智能化，这类似于人的社会分工，最终构成人类社会。\n例如都是面粉，有的做面条，有的做馒头\n\n', '单选题', 49);
