INSERT INTO `question` VALUES (802, '程序与进程的主要区别是', '程序是静态的,而进程是动态的', '程序可以脱离机器长期保存,进程则是在程序运行完毕就不存在', '一个程序可以多次执行,对应产生多个不同的进程', '进程只是程序的另一种形式', 2, 'ABC', ' 程序是指令的有序集合，是一个静态概念，其本事没有任何运行的含义。而进程是程序在处理机上的一次执行过程，是一动态的概念。\n2、  程序可以作为一种软件资料长期保存，而进程则是有一定生命周期的，它能够动态的产生和消亡。\n3、  进程是一个能独立运行的单位，能与其他进程并行活动。\n4、  进程是竞争计算机系统有限资源的基本单位，也是进行处理机调度的基本单位。程序无此该概念。\n5、  进程与程序之间无一一对应关系。不同的进程可以包含同一程序，同一程序在执行中也可以产生多个进程。\n6、  程序是记录在介质上指令的有序集合，而进程则由程序、数据和进程控制块3部分组成。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (803, '物联网具有智能属性,可进行', '智能控制', '自动监测', '自动操作', '自动修正', 2, 'ABCD', '全选\n\n', '多选题', 49);
INSERT INTO `question` VALUES (804, '题75图描述的为IP数据报的组成,其中★为首部信息,数据区中的数据最少1B,整个IP数据报最大为___', '', '', '', '', 4, '64', '\n2)首部长度。占4位。以32位为单位,最大值为60B(15×4B)。最常用的首部长度是20B,\n此时不使用任何选项(即可选字段)。\n3)总长度。占16位。指首部和数据之和的长度,单位为字节,因此数据报的最大长度为26-1\n=65535B。以太网帧的最大传送单元(MTU)为1500B,因此当一个P数据报封装成帧时,\n数据报的总长度(首部加数据)一定不能超过下面数据链路层的MTU值。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (805, '在网络信息安全中，身份鉴别建立在访问控制基础之上', '', '', '', '', 3, '错', '写反了。在网络安全环境中，访问控制能够限制和控制通过通信链路对主机系统和应用的访问。\n为了达到这种控制，每个想获得访问的实体都必须经过鉴别或身份验证，这样才能根据个体来制定访问权利。\n访问控制服务用于防止未授权用户非法使用系统资源。它包括用户身份认证，也包括用户的权限确认。\n这种保护服务可提供给用户组。', '判断题', 49);
INSERT INTO `question` VALUES (806, '操作系统文件管理的主要功能是', '实现虚拟存储', '实现按文件内容存储', '实现文件的高速输入输出', '实现按文件名存取', 1, 'D', '虚拟存储是存储管理，b错，c,\n\n', '单选题', 49);
INSERT INTO `question` VALUES (807, '针对下一代信息浪潮提出了“智慧地球”战略。', 'IBM', 'NEC', 'NASA', 'EDTD', 1, 'A', ' 2008年11月，IBM提出“智慧地球”概念，意在发挥先进信息技术潜力，促进一个产业或社会生态系统的互动，\n从而形成新的世界运行模式，“智慧的城市”、“智慧的运算”、“智慧的银行”、“智慧的医疗”、“智慧的政府”、“智慧的能源”等等，\n都是IBM“智慧地球”战略的内容。“智慧地球”推动数年，已经在全球许多领域形成态势。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (808, '从感知、通信、计算到提炼出知识,再到形成智能策略的过程称为', '智能决策', '全面感知', '智能处理', '互联网', 1, 'A', '智能策略：可能对应决策\n\n', '单选题', 49);
INSERT INTO `question` VALUES (809, '根据所连接部件的不同,总线通常被分成以下几种类型', 'I/0总线', '存储器总线', '数据总线', '前端总线', 2, 'ABD', '前端总线（FSB，Front Side Bus）是指中央处理器数据总线的专门术语，此总线负责中央处理器和北桥芯片间的数据传递。\n现在的x86处理器内置了存储器控制器，FSB已被Intel QPI和AMD HyperTransport取代。\n某些带有L2和L3缓存（Cache）的CPU，通过后端总线（Back Side Bus）实现这些缓存和中央处理器的连接，而此总线的数据传输速率总是高于前端总线。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (810, '下列属于国际标准化组织(IS0)制定的标准是', 'OSI/RM网络体系结构', 'DOLBY-AC3声音', 'JPEG图像', 'WMV视频', 2, 'AC', '杜比数字（Dolby Digital）又名杜比AC-3（Dolby AC-3），是杜比实验室开发的音频压缩技术的名称\nJPEG 2000是国际标准化组织（ISO）发布的标准\n\n', '多选题', 49);
INSERT INTO `question` VALUES (811, '下列属于射频识别技术突出特点的有', '保密性强', '抗恶劣环境', '可识别高速物体', '同时识别多个对象', 2, 'ABCD', '它有使用寿命长、安全性好等特点，其扫描快速，使用方便快捷。\nRFID在信息读取上不受芯片大小形状限制，可应用于多种不同产品，其抗污染能力强，对水和一些化学物质有很强抵抗性。\n相比于一些条形码印刷后不可更改，RFID标签可以对其内部数据进行多次修改、删除，方便信息更新。\nRFID还可以进行远距离读取，可以大大提高企业的信息管理效率。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (812, '下列属于传感设备的是', 'RFID', '红外感应器', '全球定位系统', '激光扫描器', 2, 'ABCD', '激光扫描器是一种光学距离传感器，用于危险区域的灵活防护，通过出入控制，实现访问保护等。它扫描方式有单线扫描、光栅式扫描和全角度扫描三种方式。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (813, '目前,计算机的发展方向是微型化,___,___,___。', '', '', '', '', 4, '巨型化;网络化;智能化', '计算机发展的方向是巨型化、微型化、网络化、智能化。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (814, '下列算法中均以比较作为基本运算,侧平均情况与最坏情况下的时间复杂度相同的是()', '在顺序存储的线性表中寻找最大项', '在顺序存储的线性表中进行顺序查找', '在顺序存储的有序表中进行对分查找', '在链式存储的有序表中进行查找', 1, 'A', '寻找最大项,无论如何都要查看所有的数据,与数据原始排列顺序没有多大关系,无所谓最坏情况和最\n好情况,或者说平均情况与最坏情况下的时间复杂度是相同的。而查找无论是对分查找还是顺序查找,\n都与要找的数据和原始的数据排列情况有关,最好情况是第1次查看的一个数据恰好是要找的数据,只\n需要比较1次;如果没有找到再查看下一个数据,直到找到为止,最坏情况下是最后一次查看的数据才\n是要找的,顺序查找和对分查找在最坏情况下比较次数分别是n和log2,平均情况则是1~最坏情况的\n平均,因而是不同的。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (815, '以下选项不属于可视化基本特征的是()', '易懂性', '全面性', '必然性', '专业性', 1, 'B', '可视化的基本特征:①易懂性、②必然性、③片面性、④专业性\n\n', '单选题', 49);
INSERT INTO `question` VALUES (816, '十六进制数FFF.CH相当十进制数', '4096.3', '4096.25', '4096.75', '4095.75', 1, 'D', '=FFF.CH\n=1111111111111111.1100\n=2^(12)-1+0.5+0.25\n=4095.75\n\n', '单选题', 49);
INSERT INTO `question` VALUES (817, '根据不同的应用需求,数字音频采用的编码方法有多种,文件格式也各不相同。以下属于音频格式的有()', 'WAV', 'MP3', 'FLV', 'AC3', 2, 'ABD', 'FLV是flashi的动画后缀。FLAC 无损压缩 2：1 xipa.org基金会\n\n', '多选题', 49);
INSERT INTO `question` VALUES (818, '以下关于区块链的说法中,正确的有0', '比特币的底层技术是区块链', '区块链技术是一种全面记账的方式', '区块链是加密数据按照时间顺序叠加生成临时、不可逆向的记录', '目前区块链可分为公有链、私有链、联盟链三种类型', 2, 'ABD', '区块链是一种按照时间顺序将数据区块依次链接形成的链式数据结构,并以密码学方法保证数据块的不\n可篡改不可伪造。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (819, '专家系统是以___为基础,以推理为核心的系统。', '', '', '', '', 4, '知识库', '专家系统是以(知识)为基础,以推理为核心的系统。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (820, 'Windows、安卓和iOS三种操作系统中的___文件目录(文件夹)即可以帮助户把不同类型和不同用途\n的文件分类存储,又方便了文件的查找,还可允许不相同的两个只文件夹中的文件使用相同的名字。', '', '', '', '', 4, '树状', 'Windows、安卓和iOS三种操作系统中的树状文件目录(文件夹)即可以帮助户把不同类型和不同用途\n的文件分类存储,又方便了文件的查找,还可允许不相同的两个只文件夹中的文件使用相同的名字。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (821, '在虚拟化架构中___虚拟化架构是指将一个内核级驱动器插入到宿主操作系统内核。驱动器作为虚拟硬件\n管理器来协调虚拟机和宿主操作系统之间的硬件访问。', '', '', '', '', 4, '混合', '混合虚拟化架构。将一个内核级驱动器插入到宿主操作系统内核。驱动器作为虚拟硬件管理器来协调虚\n拟机和宿主操作系统之间的硬件访问。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (822, '下列关于集线器的描述中,错误的是()。', '集线器工作于物理层,以广播的形式进行数据转发', '连接到集线器的结点发送数据时采用CSMA/CD算法', '通过在网络链路中串接一个集线器可以监听该链路中的数据包', '连接到一个集线器的所有结点独占一个冲突域', 1, 'D', '集线器工作于物理层,以广播的形式进行数据转发。结点通过双绞线连接到一个集线器上,当其中一个\n结点发送数据的时候,所有结点都可以收到消息,即链路中串接集线器可以监听该链路中的数据包,目\n每次只能有一个结点能够发送数据,而其他的结点都处于接收数据的状态。连接到集线器的所有结点共\n享一个冲突域,这些结点执行CSMA/CD介质访问控制方法。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (823, '状态图适用描述跨越多个用例的对象在其___中的各种状态及其状态之间的转换。', '', '', '', '', 4, '生命周期', '状态机是一种行为,它说明对象在其生命周期中响应事件所经历的状态变化序列以及对那些时间的响\n应。\n(2)一般情况下,一个状态机依附于一个类,用来描述这个类的实例的状态及其转换,和对接收到的\n事件所做出的响应。\n(2)此外,状态机也可以依附于用例、操作、协作等元素上,描述它们的执行过程。\n(3)状态机从对象的初始状态开始,响应事件并执行某些动作,从而引起状态的转换;在新状态下又\n继续响应事件并执行动作,如此循环进行到对象的终结状态。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (824, 'ALTER TABLE语句提供了三个修改字段的子句分别是:CHANGE、MODFIY、______', '', '', '', '', 4, 'AFTER', 'ALTER TABLE语句提供了三个修改字段的子句分别是:CHANGE、MODFIY、AFTER.\nCHANGE修改字段名称和类型\nMODIFY只修改字段类型。还可以通过FIRST和AFTER关键字修改指定列在表中的位置。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (825, '一个最多能容纳m个元素的顺序存储循环队列Q,其头尾指针分别为front和rear,则判定该队列为满的条件是()', '(Q.rear+1)%m==Q.front', 'Q.front==Q.rear', 'Q.rear+1==Q.front', '(Q.front+1)%m==Q.rear', 1, 'A', '\n满:当队列添加元素到rear的下一个元素是head的时候,也就是转圈子要碰头了,我们就认为队列满了。(Q.rear+l)%MAXSIZE=Q,front\n空:当队列除元素到head=rear的时候,我们认为队列空了。Q.rear==Q.front,,不一定为0\n\n', '单选题', 49);
INSERT INTO `question` VALUES (826, '以太坊通过()实现对状态的校验查询。', 'MerkleTree', 'MPT树', 'PoW算法', '阶段协议', 1, 'B', '哈希树（hash tree；Merkle tree），在密码学及计算机科学中是一种树形数据结构，每个叶节点均以数据块的哈希作为标签，而除了叶节点以外的节点则以其子节点标签的加密哈希作为标签 。\n哈希树能够高效、安全地验证大型数据结构的内容，是哈希链的推广形式\n哈希树的概念由瑞夫·墨克于 1979 年申请专利，故亦称墨克树（Merkle tree）。\nMPT树一种经过改良的、融合了默克尔树和前缀树两种树结构优点的数据结构，\n以太坊中，MPT是一个非常重要的数据结构，在以太坊中，帐户的交易信息、状态以及相应的状态变更，还有相关的交易信息等都使用MPT来进行管理，其是整个数据存储的重要一环。\n交易树，收据树，状态树都是采用的MPT结构。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (827, '为了区分文件的类型,文件名的最后几个(一般是3或4个)英文字母用于指出文件的具体的类型,在Nindows上\n是应用程序扩展的文件扩展名。', 'exe', 'dll', 'app', 'bmp', 1, 'B', '为了区分文件的类型,文件名的最后几个(一般是3或4个)英文字母用于指出文件的具体的类型,在\nWindows上\"dll\"是应用程序扩展的文件扩展名。\nexe 应用程序\n\n', '单选题', 49);
INSERT INTO `question` VALUES (828, '以下关于移动互联网的描述,正确的有()。', '移动互联网使得用户可以在移动状态下接入和使用互联网服务', '移动互联网是桌面互联网的复制和移植', '传感技术能极大地推动移动互联网的成长', '在移动互联网领域,仍存在浏览器竞争及“孤岛”问题', 2, 'ACD', '移动互联网的核心是互联网,因此一般认为移动互联网是桌面互联网的补充和延伸,应用和内容仍是移\n动互联网的根本。选项B描述不正确。\n其他选项没问题。利用传感技术能够实现网络由固定模式向移动模式的转变，方便广大用户。将传感技术应用到移动互联网中，极大地推动了移动互联网的成长\n\n', '多选题', 49);
INSERT INTO `question` VALUES (829, '___是用于在CPU、主存、辅存和各种输入/输出设备之间传输信息并协调它们工作的一种部件,由传输线和控制\n电路组成。', '', '', '', '', 4, '总线', '总线是用于在CPU、主存、辅存和各种输入/输出设备之间传输信息并协调它们工作的一种部件,由传\n输线和控制电路组成。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (830, '填空题,2.0分\n___技术在生活中应用随处可见,例如第二代身份证、校园卡、公交卡都使用了该技术。', '', '', '', '', 4, 'RFID', 'RFD技术在生活中应用随处可见,例如第二代身份证、校园卡、公交卡都使用了该技术。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (831, '采用IEEE802.11b标准将两栋楼内的局域网互连为一个逻辑网络,应使用的无线设备是()', '无线网桥', '无线路由器', '无线接入点', '无线网卡', 1, 'A', '无线接入点也就是无线AP,用于集合无线或者有线终端,作用类似于集线器和交换机。\n无线路由器是具有路由功能的AP,用于建立一个更小的无线局域网。\n无线网桥主要用于连接几个不同的网段,实现较远距离的无线数据通信。无线网卡,用于两个拥有无线\n网卡的计算机间点对点通信,组成一个最小的无线局域网,所以此题选A。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (832, '若要求从键盘读入含有空格字符的字符串,应用该使用函数', 'getc()', 'gets()', 'getchar()', 'scanf()', 1, 'B', 'gets()可以读入含有空格字符的字符串。getchar()显示单个。scanf()空格可以作为分隔符\n\n', '单选题', 49);
INSERT INTO `question` VALUES (833, '802.11b只是将传输度提高到___', '', '', '', '', 4, '11Mbps', '802.11b定义了使用直序扩频技术,传输速率为1Mbps、2Mbps、5.5Mbps与11Mbps的无线局域网\n标准。\n将传输速率提高到54Mbps的是802.11a和802.11g,而802.11b只是将传输速度提高到11mbps。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (834, '参加位运算的数据可以是任何类型的数据。', '', '', '', '', 3, '错', '程序中的所有数在计算机内存中都是以二进制的形式储存的。位运算就是直接对整数在内存中的二进制位进行操作，而浮点数是无法精确的转化为二进制表示的，所以是无法进行位运算的。', '判断题', 49);
INSERT INTO `question` VALUES (835, 'TCP-IP协议体系结构组织的相对独立层次是()。', '四层', '五层', '六层', '七层', 1, 'A', 'TCP-IP协议体系结构组织4;iso 7;\n\n', '单选题', 49);
INSERT INTO `question` VALUES (836, '不适合交互式通信的交换技术是()。', '报文', '分组', '电路', '虚电路分组', 1, 'A', '报文从源点传送到目的地采用存储转发的方式，报文需要排队，因此报文交换不适合交互式通信，不能满足实时通信的要求，\n\n', '单选题', 49);
INSERT INTO `question` VALUES (837, '用户程序发出磁盘/O请求后,系统的正确处理流程是()。', '用户程序→系统调用处理程序→中断处理程序→设备驱动程序', '用户程序→系统调用处理程序→设备驱动程序→中断处理程序', '用户程序→设备驱动程序→系统调用处理程序→中断处理程序', '用户程序→设备驱动程序→中断处理程序→系统调用处理程序', 1, 'B', 'I/O\n软件一般从上到下分为4个层次：\n用户层、与设备无关软件层、设备驱动程序层以及中断处理程序层。\n用户层软件必须通过一组系统调用来\n取得操作系统服务。\n与设备无关软件层也是系统调用的处理程序，\n所以正确处理流程\n为B选项。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (838, '数据库恢复的基础是利用转储的冗余数据。这些转储的冗余数据是指()。\nA数据字典、应用程序、审计档案、数据库后备副本', '数据字典、应用程序、日志文件、审计档案', '日志文件、数据库后备副本', '数据字典、应用程序、数据库后备副本', '', 1, 'C', '转存数据冗余一般是日志文件和数据库后备副本。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (839, '并发进程之间()。', '彼此无关', '必须同步', '必须互斥', '可能需要同步或互斥', 1, 'D', '并发进程之间可能存在同步或互斥关系。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (840, '数据库物理设计完成后,进入数据库实施阶段,下列各项中不属于实施阶段的\n工作是()。', '建立库结构', '扩充功能', '加载数据', '系统调试', 1, 'B', '数据库实施阶段：运用DBMS提供的数据语言、工具及宿主语言，\n根据逻辑设计和物理设计的结果建立数据库，编制与调试应用程序，\n组织数据入库，并进行调试运行。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (841, '一般而言,计算机中()容量(个数)最多。', 'ROM', 'RAM', 'CPU', '虚拟存储器', 1, 'D', '虚拟存储器对内存进行了逻辑扩充，\n\n', '单选题', 49);
INSERT INTO `question` VALUES (842, '将一个递归算法改为对应的非递归算法时,通常需要使用()。', '栈', '队列', '循环队列', '优先队列', 1, 'A', '递归程序在运行时就是将当前函数的信息压入栈中进入下一层函数，下一层函数调用完毕后将栈中信息弹出继续当前函数计算\n将递归函数变为非递归函数可以自己定义栈来模拟该过程实现\n\n', '单选题', 49);
INSERT INTO `question` VALUES (843, '关于网络分层的概念,下面说法错误的是()。', '上层使用下层提供的服务', '网络中两台主机的对等层之间是直接进行通信的', '分层后各层易于实现和维护,能促进标准化工作', '协议是“水平”的,服务是“垂直”的', 1, 'B', '网络中两台主机的对等层之间是逻辑上的通信，不能直接进行通信，\n需要经过各个层次的作用，然后经过物理层的实际传输才能完成。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (844, '下列PV6地址的表示中,合法地址是()。', 'FB03::02', '::2C57::EF01', '18E6::8F21', 'FFFF::2A:DO:0', 2, 'ACD', 'FFFF::2A:DO:0 IANA特殊地址 多播地址;未指定地址为 0:0:0:0:0:0:0:0\n回送地址为 0:0:0:0:0:0:0:1\n\n\n', '多选题', 49);
INSERT INTO `question` VALUES (845, '下面关于计算机软件的描述错误的是()。', '它是系统赖以工作的实体', '它是指计算机的程序及文档', '位于计算机系统的最外层', '分为系统软件和支撑软件两大类', 2, 'ACD', '计算机软件( Software，也称软件)是指计算机系统中的程序及其文档\n\n', '多选题', 49);
INSERT INTO `question` VALUES (846, '下列关于数据库系统的正确叙述是()。', '数据库系统减少了数据冗余', '数据库系统避免了一切冗余', '数据库系统中数据的一致性是指数据类型一致', '数据库系统比文件系统能管理更多的数据', 1, 'A', '数据库系统的基本要求是：保证数据的独立性、减少数据冗余、\n使用户的接口简单和保证系统运行可靠。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (847, '数据库的特点之一是数据的共享,严格地讲,这里的数据共享是指()。', '同一个应用中的多个程序共享一个数据集合', '多个用户、同一种语言共享数据', '多个用户共享一个数据文件', '多种应用、多种语言、多个用户相互覆盖地使用数据集合', 1, 'D', '数据共享就是让在不同地方使用不同计算机、不同软件的用户能够读取他人数据并进行各种操作、运算和分析。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (848, '以下关于DBA职责的叙述中,不正确的是()。', 'DBA是数据库系统的超级用户,负责控制和管理各个用户的访问权限', 'DBA要负责监控数据库的运行', 'DBA要配合前端应用程序的开发', 'DBA要负责当数据库系统发生故障时进行恢复', 1, 'A', '数据库管理员从事管理和维护数据库系统。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (849, '异步传递模式ATM采用称为信元的()分组,并使用信道传输。', '定长,光纤', '定长,双绞线', '变长,双绞线', '变长,光纤', 1, 'A', '异步传递模式ATM的信元长度为53个字节，为定长分组,并且采用光纤进行传输。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (850, '下面程序段的时间复杂度为()。\nfor (int i=0;i<m;i++)\n    for(int j=0;j<n;j++)\n        a[i][j]=i*j;', '0(m2)', '0(n2)', '0(m*n)', 'O (m+n)', 1, 'C', '基本操作语句为a[i][j]=i*j；，共执行m*n次。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (851, '下面程序段的时间复杂度为（）。\nint f（unsigned int n）{\n    If（n==0||n==1）\n        return1;\n    else：\n    return n*f（n-1）;\n}', 'O（1）', 'O（n）', 'O（n2）', 'O（n!）', 1, 'B', '这是一个递归函数，函数共执行n次。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (852, '在下面的叙述中,正确的是', '引入线程后,处理机只在线程间切换', '引入线程后,处理机仍在进程间切换', '线程的切换,不会引起进程的切换\nD线程的切换,可能引起进程的切换', '', 1, 'D', '同一进程内的线程切换不会引起进程的切换，不同进程内线程的切换是会引起进程的切换的。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (853, '数据库管理系统的发展历经了如下那些模型阶段()。', '层次模型', '结构模型', '关系模型', '网状模型\nE.对象模型\n E', 2, 'ACD', '结构模型不是数据库管理系统的发展阶段。\n第一代数据库系统：层次和网状模型，层次模型数据库管理系统、网状模型数据库管理系统；\n第二代数据库系统：关系模型，关系数据库系统；\n第三代数据库系统：面向对象模型；\n\n', '多选题', 49);
INSERT INTO `question` VALUES (854, '计算机网络的主要功能有()。', '数据通信', '数据共享', '负荷均衡', '分布处理\nE.软硬件共享\n E', 2, 'ABCD', '计算机网络功能主要包括实现资源共享，实现数据信息的快速传递，提高可靠性，提供负载均衡与分布式处理能力，集中管理以及综合信息服务。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (855, '数码相机也是一种图像输入设备,其中的成像芯片CCD或CMOS主要进行电信号到数\n字信号的处理,无需使用胶卷', '', '', '', '', 3, '错', '数码相机与普通照相机在胶卷上靠溴化银的化学变化来记录图像的原理不同，数字相机的传感器是一种光感应式的电荷耦合器件(CCD）或互补金属氧化物半导体(CMOS）。', '判断题', 49);
INSERT INTO `question` VALUES (856, ')平时人们所说的“声卡”,实际上就是声音I/0控制器,一般在PC机中采用“集成”\n形式,只有专业用的高档声卡才做成独立的插卡形式。', '', '', '', '', 3, '对', '实际上就是声音I/0控制器,', '判断题', 49);
INSERT INTO `question` VALUES (857, '下列有关域名的说法,正确的是', '域名就是用人性化的名字来表示主机地址', '一个域名由若干部分组成,各部分用.分隔,第一部分是顶级域名,cn表示中国', '完成“名字一一地址”映射的过程叫做反向解', 'Internet上计算机的IP地址有两种表示形式:IP地址和域名', 1, 'A', 'ip地址-->域名是反向解析\n\n', '单选题', 49);
INSERT INTO `question` VALUES (858, '移动互联网的产业模型不包括', '终端制造商', '电信运行商', '服务提供商', '芯片制造商', 2, 'ABC', '芯片制造商是个基础产业，是其它三个的基础\n\n', '多选题', 49);
INSERT INTO `question` VALUES (859, '下列属于文字处理软件的是', 'Word', 'Acrobat', 'WPS', '记事本', 2, 'ABCD', 'Adobe Acrobat是由Adobe公司所开发的电子文字处理软件集，可用于阅读、编辑、管理和共享PDF文档。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (860, '下面关于移动互联网核心网描述,正确的是', '核心网一般均包含接入路由器和网关', '核心网负责管理用户移动信息', '核心网保证用户在移动中连接不中断', '核心网为用户提供互联网应用服务', 2, 'ABC', '无　\n\n', '多选题', 49);
INSERT INTO `question` VALUES (861, '下列叙述正确的是', '移动互联网常见的开发语言包含有Objective C和C++', '移动互联网开发所面临的问题包括兼容和性能', '移动互联网开发的数据有限但更新快', '移动互联网开发的成本包括学习成本和设备成本', 2, 'ABD', '无　\n\n', '多选题', 49);
INSERT INTO `question` VALUES (862, '与以前操作系统使用的字符方式界面不同,Windows7操作系统采用图像用户界面,称为GUI。在该环境下,操作系统采用来___形象地表示系统中的文件、程序、设备等对象。', '', '', '', '', 4, '图标', '无\n\n', '填空题', 49);
INSERT INTO `question` VALUES (863, '下面四种排序算法中,稳定的算法是()', '基数排序', '归并排序', '堆排序', '希尔排序', 2, 'AB', '选择排序、快速排序、希尔排序、堆排序不是稳定的排序算法；\n冒泡排序、插入排序、归并排序和基数排序都是稳定的排序算法。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (864, '数据压缩一般有无损压缩和有损压缩,行程长度编码、哈夫曼编码还原的图像与原始图像有一定误差,属有损压缩编码。', '', '', '', '', 3, '错', '无损压缩利用数据的统计冗余进行压缩,可以保证在数据压缩和还原过程中,图像信息没\n有损耗或失真,图像还原(解压缩)时可完全恢复(即重建后的图像与原始图像完全相同)。\n例如,在多媒体应用中常用行程长度编码(RLE)、增量调制编码(DM)、霍夫曼(Huffman)\n编码等。', '判断题', 49);
INSERT INTO `question` VALUES (865, '下列有关程序与软件的叙述中,错误的是', '软件版权的所有者,也就是软件的开发者,唯一地享有该软件的拷贝、发布、修\n改、署名、出售等诸多权利', '人们一般把程序以及与程序相关的数据和文档统称为软件,通常软件(特别是商\n品软件和大型软件)必须有完整、规范的文档作为支持', '计算机的通用性和应用的广泛性主要体现在:一是可以运行不同的程序以实现不\n同的功能,二是同一程序可以处理不同的数据', '无论是下载的软件,还是光盘或者U盘等辅助存储器中的软件,通常情况下需要\n在PC或智能手机等设备上安装之后才能工作', 1, 'C', '计算机的通用性表现在：由于计算机的可编程性，计算机能够在各行各业得到广泛的应用,\n这题是计算机的同用性，而不是程序的通用性，李代桃僵！\n\n', '单选题', 49);
INSERT INTO `question` VALUES (866, '移动互联网的关键问题不包括', '随时', '随地', '服务', '通信技术', 1, 'D', '通信技术太泛\n\n', '单选题', 49);
INSERT INTO `question` VALUES (867, '围绕着信息技术的发展和应用,逐步形成了信息产业。下列有关信息产业的叙述中,\n正确的是', '进入21世纪以来,信息产业已经成为全球第一大产业', '信息产业是战略性先导产业,具有高投入、高风险和增长快、变动大的特点', '信息产业通常分为信息设备制造业、信息服务业、信息开发业等', '软件产业、电子出版业、游戏与动漫产业等通常归于信息服务业范畴', 2, 'ABC', '　　信息服务业是一个涉及信息生产、分发与信息供给等众多领域的综合性行业\n\n', '多选题', 49);
INSERT INTO `question` VALUES (868, '移动互联网的特点包括', '移动互联', '碎片化', '互联网思维', '无中心', 2, 'ACD', '无　\n\n', '多选题', 49);
INSERT INTO `question` VALUES (869, '用HTML5进行移动互联网开发的准备工作包括', '开发的相关设备', '开发环境', '开发帐号', '开发与管理标准', 2, 'ABC', '无　\n\n', '多选题', 49);
INSERT INTO `question` VALUES (870, '数据加密前的原始数据(消息)称为明文,加密后的数据称为密文,将两者数据进行相互转换的算法称为___。', '', '', '', '', 4, '密码', '在密码学中，密码（英语：cipher，或cypher），是一种用于执行加密或解密的算法\n\n', '填空题', 49);
INSERT INTO `question` VALUES (871, '所谓的()是指将一个以上的作业放入内存并同时处于运行状态,这些作业\n共享计算机系统的所有资源。', '多重处理', '并行执行', '实时处理', '多道程序设计', 1, 'D', '④多道程序设计一是指允许多个程序同时进入内存并运行。多道程序设计是操作\n系统中一个最基本、最重要的概念,引入的根本目的是提高系统效率。衡量系统效率的\n尺度是系统吞吐量。吞吐量是指单位时间内系统所处理作业的数量。引入多道程序设计\n的最终目的是提高系统的吞吐量。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (872, 'OSPF（开放的最短路径优先）采用的度量是（	）。', '跳数	B.带宽', '负载	D.延迟', '', '', 1, 'B', '开放最短路径优先 （OSPF）协议是一种链路状态路由协议，旨在替代距离矢量路由协议 RIP。OSPF 是一种无类路由协议，它使用区域概念\n实现可扩展性。RFC 2328 将 OSPF 度量定义为一个独立的值，该值称为开销。 使用带宽作为 OSPF 开销度量\n\n', '单选题', 49);
INSERT INTO `question` VALUES (873, '用链接方式存储的队列，在进行插入运算时（	）。', '仅修改头指针	B.头、尾指针都要修改', '仅修改尾指针	D.头、尾指针可能都要修改', '', '', 1, 'D', '插入元素时通常修改尾指针，当插入前为空时，做插入操\n作，头尾指针都需要修改。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (874, '某寄存器中的值有时是地址，因此只有计算机的（	）才能识别它。', '译码器	B.判断程序', '指令	D.时序信号', '', '', 1, 'C', '指令由操作码和地址码两部分组成，当寄存器中的值是地址时，只有计算机的指令才能识别它。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (875, 'CPU  中的数据总线宽度会影响（	）。', '内存容量的大小	B.系统的运算速度', '指令系统的指令数量	D.寄存器的宽度', '', '', 1, 'B', '数据总线的宽度表示同时传送的二进制位数，内存容量、指令系统中的指令数量和寄存器的位数与数据总线的宽度无关。\n数据总线的宽度越大，单位时间能进出 CPU 的数据就越多，系统的运算速度越快。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (876, '计算机信息系统中数据库访问通用采用客户/服务器(C/S)模式和B/S模式,后者是三层模式,该模式实际上是增加了___的C/S模式。', '', '', '', '', 4, 'web', '无\n\n', '填空题', 49);
INSERT INTO `question` VALUES (877, '一个用于发送二进制信号的信道带宽为 3kHz，其信噪比为 20dB，那么，最大数据传输速率为（	）b/s。', '3000×log2(21)	B.3000×log2(101)', '3000	D.6000', '', '', 1, 'B', '信噪比为 20，根据信噪比公式，可知信息功率与噪音功率之比为 100，最大传输速率 C=3000* log2（1+100），选 B。\n信噪比：（dB）= 10 * log10(S/N) (dB)\n 香农公式：C=Wlog2(1+S/N) b/s\n\n', '单选题', 49);
INSERT INTO `question` VALUES (878, '磁盘存储器的记录方式一般采用（	）。', '归零制	B.不归零制', '调频制	D.调相制', '', '', 1, 'C', '调相制一般用于磁带存储器上，我认为答案错了 而书上对归零制强调已经淘汰，对不归零的没有任何应用的解释 对未改进的调频制说应用在硬盘和软盘上很广，我就觉得改进的应该也差不多吧\n\n', '单选题', 49);
INSERT INTO `question` VALUES (879, '以下与数据的存储结构无关的术语是（	）。', '循环队列	B.链表', '哈希表	D.栈', '', '', 1, 'D', '存储结构是数据的逻辑结构用计算机语言的实现，\n常见的存储结构有：顺序存储，链式存储，索引存储，以及散列存储。\n其中散列所形成的存储结构叫散列表（又叫哈希表），因此哈希表也是一种存储结构。\n栈只是一种抽象数据类型，是一种逻辑结构，栈逻辑结构对应的顺序存储结构为顺序栈，对应的链式存储结构为链栈，\n循环队列是顺序存储结构，链表是线性表的链式存储结构。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (880, '计算机发展的方向之一是“巨型化”，“巨型化”的含义是指（	）。', '重量重 B.体积大 C.设备更多', '功能更强、运算速度更快和存储容量更大', '', '', 1, 'D', '也对以前的远古神机也很大，但不能称为巨型化\n\n', '单选题', 49);
INSERT INTO `question` VALUES (881, '下列描述中错误的是', 'GPU是显卡的核心', '程序是软件的核心', '计算机软件及通信技术是现代信息技术的核心', '网络通讯中,访问控制是身份鉴别的核心', 1, 'D', '身份认证是解决你是谁，你对应的角色role是什么，即通过你的User name + User password，确定你是合法的某个user id，然后user id对应（map）到一个role（通过一个映射表map table实现）。\n访问控制是什么资源给什么role。比如：role administrator，有全部的访问资源权限。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (882, '下列有关计算机动画的描述,错误的是', '计算机动画是一种计算机合成的虚拟场景的数字视频', '在使用微信软件发送内容时,gf技术可帮助用户实现一些生动活泼的小动\n画,Flash技术可实现类似发送“生日快乐”文字后产生“蛋糕从上方飘下\n来”的效果', '3DMAX、MAYA都是三维动画软件', '计算机动画可转换为电视或电影输出', 1, 'B', '可能是css后者是JavaScript\n\n', '单选题', 49);
INSERT INTO `question` VALUES (883, '下列关于分布式系统的叙述中,不正确的是', '分布式操作系统是一个统一的操作系统', '分布式系统中的各个计算机有主次之分', '分布式系统中各个计算机相互件共同完成一项任务', '分布式操作系统有更高的可靠性', 1, 'B', '1.3 分布式系统特性\n分布性\n空间中随机分布。这些计算机可以分布在不同的机房，不同的城市，甚至不同的国家。\n对等性\n分布式系统中的计算机没有主/从之分，组成分布式系统的所有节点都是对等的。\n并发性\n同一个分布式系统的多个节点，可能会并发地操作一些共享的资源，诸如数据库或分布式存储。\n缺乏全局时钟\n既然各个计算机之间是依赖于交换信息来进行相互通信，很难定义两件事件的先后顺序，缺乏全局始终控制序列\n故障总会发生\n组成分布式的计算机，都有可能在某一时刻突然间崩掉。分的计算机越多，可能崩掉一个的几率就越大。如果再考虑到设计程序时的异常故障，也会加大故障的概率。\n处理单点故障\n单点SPoF（Single Point of Failure）：某个角色或者功能只有某一台计算机在支撑，在这台计算机上出现的故障是单点故障。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (884, '____是一整套复杂的设施。它不仅仅包括计算机系统和其它与之配套\n的设备,还包含冗余的数据通信连接、环境控制设备、监控设备以及各种安\n全装置。', '公有云', '私有云', '混合云', '数据中心', 1, 'D', '数据中心 (英语: Data center)，指用于安置计算机系统及相关部件的设施，例如电信和储存系统。\n一般它包含冗余和备用电源，冗余数据通信连接，环境控制（例如空调、灭火器）和各种安全设备。大型数据中心消耗的电约与一个小城镇工业业务规模一样多。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (885, '同一物理机上运行的虚拟机,其中一台发生故障不影响其他虚拟机运行,该\n描述体现虚拟机的\n特性。', '分区', '隔离', '封装', '相对硬件独立', 1, 'B', '分区：强大的服务器倍分为多个服务器\n隔离：物理化虚拟服务器，相互独立，隔离；数据不会在虚拟机之间泄露\n封装：虚拟单元的所有环境存放在一个单独的文件中\n硬件独立：可以在其它的服务器不加修改运行虚拟机\n\n', '单选题', 49);
INSERT INTO `question` VALUES (886, '目前,选用开源的虚拟化产品组建虚拟化平台,构建基于硬件的虚拟化层,可以选用', 'Xen', 'VMware', 'Hyper-v', 'Citrix', 1, 'A', '一、开源虚拟化平台\nXen和KVM（Xen是剑桥大学开发的，KVM是以色列一家公司开发的）\n三、QEMU qemu模拟IO设备（网卡或磁盘等等），他可以模拟出VM需要的任何资源\n', '单选题', 49);
INSERT INTO `question` VALUES (887, '按照信息系统的定义,下面所列的应用中,属于管理业务系统的是', '民航订票系统', '银行信用卡支付系统', 'Web信息检索系统', '人事管理系统', 2, 'ABD', '无\n\n', '多选题', 49);
INSERT INTO `question` VALUES (888, '新建的数据库至少包含（	）数据。', '主要数据文件	B.次要数据文件', '日志文件	D.系统文件', '', '', 2, 'AC', 'SQL Server 数据库及其包含的全部对象，作为一个整体都保存在外存上相应的数据库文件中。\n在存储时，SQL Server 数据库包含两类文件：数据文件和事务日志文件。\n数据文件用于存储数据库的数据和各种对象，事务日志文件用于存储有关数据库操作信息的事务日志。\n一个数据库至少有一个数据文件和一个日志文件。可以有多个数据文件和多个日志文件。\n数据文件分为主数据文件和辅助数据文件两种类型形式。每个数据库都有且仅有一个主数据文件。\n辅助数据文件或称次数据文件是可选的，用于将数据和数据库对象分散存储在不同的文件乃至不同的存储设备上，以提高数据库安全性和处理性能。\n次数据文件由用户根据需要定义，可以是 0到多个。\n\n', '多选题', 49);
INSERT INTO `question` VALUES (889, '数据库管理系统中,能实现对数据库中的数据进行插入、修改、的功能\n称为', '数据定义功能', '数据管理功能', '数据操作功能', '数据控制功能', 1, 'C', 'DML（Data Manipulation Language）数据操纵语言\n3.DCL（Data Control Language）数据库控制语言  授权，角色控制等\nGRANT 授权\nREVOKE 取消授权\n4.TCL（Transaction Control Language）事务控制语言\nSAVEPOINT 设置保存点\nROLLBACK  回滚\nSET TRANSACTION\n\n', '单选题', 49);
INSERT INTO `question` VALUES (890, '____在五个方面有出色的表现:数据安全;服务质量(SLA);充分利用现有硬件资源;支持定制和遗留应用;不影响现有IT管理的流程。', '公有云', '私有云', '混合云', '行业云', 1, 'B', '2、私有云（搭建）\n1、构筑防火墙之后，数据安全\n2、SLA（服务质量）稳定性更高\n3、充分利用现有硬件资源和软件资源\n4、不影响现有IT管理的流程\n3、桌面云（搭建）\n1、通过web管理平台创建云桌面、云存储只需几秒钟，大大简化企业的IT维护工作；\n2、方便用户接入桌面，定云提供瘦终端，软件终端等多种用户接入方式；\n3、后端数据提供数据备份和恢复功能，提高可靠性；\n4、同一用户可以拥有多个云桌面，在终端机上可以无缝切换，方便用户工作；\n5、计算资源弹性扩展，用户桌面内存、磁盘不够用时，可以方便的进行扩容；\n6、降低总体拥有和维护成本。\n\n', '单选题', 49);
INSERT INTO `question` VALUES (891, '数据中心大多以___的形式向外提供服务,该服务一般采用三层架构,从前端到后端依次为表现层、业务逻辑层和数据访问层。', '客户端', 'Web', 'APP', '终端', 1, 'B', '无\n\n', '单选题', 49);
INSERT INTO `question` VALUES (892, '对于二叉查找树(Binary Search Tree),若其左子树非空,则左子树上所有结点值均不大于根结点值;若其右子树非空,则右子树上所有结点值均不\n不大于根结点值;左、右子树自身就是两棵二叉查找树。因而,对任意一棵\n二叉查找树进行___遍历可以得到一种结点元素递增序列。在具备n\n个结点二叉查找树上进行查找运算,最坏状况下算法复杂度为___', '', '', '', '', 4, '中序;O(n)', '最坏时二叉排序树,二叉查找树退化为单枝树，只能从根开始一层一个查找，实质变为顺序查找，时间复杂度为O(n)\n平衡二叉树，其时间复杂度即为树的高度，所以为log2n。\n\n', '填空题', 49);
INSERT INTO `question` VALUES (893, '以下关于云计算机叙述中,正确的是', '云计算通过互联网来提供动态易扩展且经常是虚拟化资源的计算模式', '云计算可以脱离网络提供服务', '云计算比本地计算具备更好的扩展性', '云计算使得计算能力成为一种在互联网上流通的资源', 2, 'ACD', '无\n\n', '多选题', 50);
INSERT INTO `question` VALUES (894, '云计算的可配置计算资源共享池包括网络___存储、____服务等资源。', '', '', '', '', 4, '服务器;应用软件', '云计算是一种按使用量付费的模式，这种模式提供可用的、便捷的、按需的网络服务（即随时随地接入可接入），\n进入可配置的计算资源共享池（包括：网络、服务器、存储、应用软件、服务），\n这些资源能够被快速提供，只需投入很少的管理工作，或与服务供应商进行很少的交互\n\n', '填空题', 50);
INSERT INTO `question` VALUES (895, 'Windows Media媒体播放器是一个通用的数字媒体播放程序,可以播放音视频文件,\n也可用来显示图片,并支持从CD光盘上抓取单轨复制到硬盘', '', '', '', '', 3, '对', 'Windows Media Player还具有以下功能： 撕裂 音乐从复制音乐到 光盘，在其中刻录可刻录光盘 音频CD 格式或作为带有播放列表的数据光盘，\n例如 MP3 CD，与数字音频播放器（MP3播放器）或其他移动设备同步内容，并使用户能够从许多 在线音乐商', '判断题', 50);
INSERT INTO `question` VALUES (896, '自然语言是人造语言,是一种为某些特定目的而创造的语言。', '', '', '', '', 3, '错', '自然语言（英语：Natural language）通常是指一种自然地随文化演化的语言。\n汉语、英语、法语、西班牙语、葡萄牙文、日语、韩语、意大利文、德文为自然语言的例子。\n而世界语则为人工语言，即是一种由人特意为某些特定目的而创造的语言。\n不过，有时所有人类使用的语言（包括上述自然地随文化演化的语言，以及人工语言）都会被视为“自然”语言，\n以相对于如编程语言等为计算机而设的“人造”语言。这一种用法可见于自然语言处理一词中。\n自然语言是人类交流和思维的主要工具。', '判断题', 50);
INSERT INTO `question` VALUES (897, '下列有关动态网页、静态网页的叙述,错误的是', '动态网页具有动画、滚动字幕等动态效果', '动态网页可以是纯文字内容,也可以是包含各种动画的内容', '淘宝网、京东商城等信息处理应用系统平台都使用动态网页技术开发实现', '动态网页在生成过程中需要访问数据库', 1, 'A', '\n所谓的动态网页，是指跟静态网页相对的一种网页编程技术。静态网页，随着html代码的生成，页面的内容和显示效果就基本上不会发生变化了——除非你修改页面代码。而动态网页则不然，页面代码虽然没有变，但是显示的内容却是可以随着时间、环境或者数据库操作的结果而发生改变的。\n值得强调的是，不要将动态网页和页面内容是否有动感混为一谈。这里说的动态网页，与网页上的各种动画、滚动字幕等视觉上的动态效果没有直接关系，动态网页也可以是纯文字内容的，也可以是包含各种动画的内容，这些只是网页具体内容的表现形式，无论网页是否具有动态效果，只要是采用了动态网站技术生成的网页都可以称为动态网页。\n\n', '单选题', 50);
INSERT INTO `question` VALUES (898, '下列哪项不在人工智能系统的知识所包含的要素中', '事实', '规则', '控制和元知', '关系', 1, 'D', '无\n\n', '单选题', 50);
INSERT INTO `question` VALUES (899, '属于人工智能应用领域的是', '自动驾驶', '智能搜索引擎', '人脸识别', '3D打印', 2, 'ABC', '智能搜索引擎是结合了人工智能技术的新一代搜索引擎。他除了能提供传统的快速检索、相关度排序等功能，还能提供用户角色登记、用户兴趣自动识别、内容的语义理解、智能信息化过滤和推送等功能。\n\n', '多选题', 50);
INSERT INTO `question` VALUES (900, '计算智能是人工智能研究的新内容，涉及____计算、___计算和进化计算等。', '', '', '', '', 4, '神经;模糊', '无\n\n', '填空题', 50);
INSERT INTO `question` VALUES (901, '人工智能中的产生式系统的推理可以分为___和____两种基本方式。', '', '', '', '', 4, '正向推理;反向推理', '无\n\n', '填空题', 50);
INSERT INTO `question` VALUES (902, '人工智能一词是在什么时间提出的()', '20世纪80年代', '20世纪70年代', '20世纪60年代', '20世纪50年代', 1, 'D', '人工智能发展历程\n(1)形成阶段(1950年代):AI元年是1956年。1956年夏季,美国的一些年青科学家在Dartmounth\n大学召开了一个夏季讨论会,在该次会议上,第一次提出了”人工智能”这一术语。\n(2)快速发展阶段(1970年代):以专家系统为代表\n(3)发展中期阶段(1980年代):以第五代计算机为代表\n(4)高速发展阶段(1988年代及以后):以神经网络为代表\n\n', '单选题', 50);
INSERT INTO `question` VALUES (903, '人工神经网络是一种进行分布式并行信息处理的算法数学模型,可以模仿人类的()', '大脑活动', '神经元', '思维', '精神世界', 1, 'B', '通过借鉴和参考神经元的机制,科学家们模拟出了人工神经元和人工神经网络。神经网络的本质是一个\n目标简单函数经过多层嵌套叠加形成的一个数学模型,通过调整网络中的参数(仅值w1)与激活函数g\n来拟合输入与输出之间的真实函数关系。\n\n', '单选题', 50);
INSERT INTO `question` VALUES (904, '人工智能是一个很广阔的领域,很多程序设计语言都可以用于人工智能开发、下列给出的程序设计语言中,主要是为\n人工智能而设计的程序设计语言是', 'Ada', 'LISP', 'Objective-C', 'FORTRAN', 1, 'B', 'Ada语言主要用于飞行器控制之类的软件中;Objective-C用于苹果手机编程:Fortran,用于数值计算语\n言。\n\n', '单选题', 50);
INSERT INTO `question` VALUES (905, '同轴电缆的的用途是()', '固定电话的本地回路', '固定电话的中继线路', '电话、电视等通信系统的远程干线', '计算机局域网', 1, 'B', '\n介质类型        特点                                      应用\n双绞线     成本低,易受外部高频电磁波干扰,误码率较高;传输距离有限    固定电话本地回路、计算机局域网\n同轴电缆    传输特性和屏蔽特性良好,可作为传输干线长距离传输载波信号    固定电话中继线路、有线电视接入\n            ,但成本较高\n光缆      传输损耗小,通讯距离长,容量大,屏蔽特性好,不易被窃听,重量轻,    电话、电视等通信，系统的远程干线,计算机网络的干线,互联网接入\n            便于铺设。缺点是强度稍差,精确连接两根光纤比较困难\n\n自由空间    使用微波、红外线、激光等,建设费用低,抗灾能力强,容量大,        广播,电视,移动，通信系统,计算机，无线局域网\n           无线接入使得通信更加方便.但易被窃听、易受干扰\n\n', '单选题', 50);
INSERT INTO `question` VALUES (906, '等语言技术、标准、协议?', 'XML', 'SOAP', 'WSDL', 'UDDI', 2, 'ABCD', 'BCD选项都基于选项A。\n\n', '多选题', 50);
INSERT INTO `question` VALUES (907, '以下属于PaaS的有()', 'Google App Enine(GAE)', 'Baidu App Eine(BAE)', 'Amazon EC2', 'OpenStack', 2, 'AB', 'C,D选项属于laaS。\n\n', '多选题', 50);
INSERT INTO `question` VALUES (908, 'URL用来标识WWW网中的每一个信息资源,不同的信息资源对应不同的URL', '', '', '', '', 3, '对', '无', '判断题', 50);
INSERT INTO `question` VALUES (909, '填空题,2.0分我国电视采用____制式,帧频为25fps,电影的帧频是24fps。', '', '', '', '', 4, 'PAL', '我国电视采用PAL制式,帧频为25fps,电影的帧频是24fps。\n最后一个字母是L。\n\n', '填空题', 50);
INSERT INTO `question` VALUES (910, '____是一种将文字符号进行图形化表示的方法,通过使用特定的扫描设备可以对其进行识读', '', '', '', '', 4, '条形码', '条形码是一种将文字符号进行图形化表示的方法,通过使用特定的扫描设备可以对其进行识读。\n\n', '填空题', 50);
INSERT INTO `question` VALUES (911, '城域网是网络运营商在城市范围内组建的一种高速宽带网络。用于城市范围内大量的局域网和个人计算机计入互联\n网城域网的英文标识是____', '', '', '', '', 4, 'MAN', '局域网:LAN\n城域网:MAN\n广域网:WAN\n\n', '填空题', 50);
INSERT INTO `question` VALUES (912, 'RFD技术俗称为电子标签,是一种非接触式的自动识别技术(基于雷达技术发展现来的),可识别\n运动____物体并可同时识别多个标签,操作快捷方便,而且识别工作无需人工干预,可在各种恶劣条件下工作。', '', '', '', '', 4, '高速', 'RFD技术俗称为电子标签,是一种非接触式的自动识别技术(基于雷达技术发展现来的),可识别高\n速运动物体并可同时识别多个标签,操作快捷方便,而目识别工作无需人工干预,可在各种恶劣条件下工\n作。\n\n', '填空题', 50);
INSERT INTO `question` VALUES (913, '在UML中,实体类表示()', '系统将跟踪的永久信息', '参与者与系统之间的交互', '负责用例的实现', '显示系统状态', 1, 'A', '分析类的类型:\n①、实体类:表示系统存储和管理的永久信息。\n②、边界类:表示参与者与系统之间交互。\n③、控制类:表示系统在运行过程中的业务控制逻辑,负责用例的实现。\n\n', '单选题', 50);
