INSERT INTO `question` VALUES (974, '用下面的T-SQL语句建立一个基本表：\n    CREATE TABLE Student(Sno CHAR(4) PRIMARY KEY,\n    Sname CHAR(8)NOT NULL,\n    Sex CHAR(2)，\n    Age INT)\n    可以插入到表中的元组是______。', '\'5021\',NULL,男,21', '\'5021\',\'刘祥\',NULL,NULL', '\'5021\',\'刘祥\',男,21', 'NULL,\'刘祥\',NULL,21', 1, 'B', ' sno是主键，不能为空。Sname也要求非空。排除AC。C项“男”字符非法，故选B。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (975, '操作系统中用得最多的数据结构是______。', '树', '表格', '队列', '堆栈', 1, 'A', '  操作系统\n[解析] 操作系统中文件的存储和管理主要是树形的目录结构，当然对于进程(线程)或走远的管理过程也是树形的结构。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (976, '关系规范化中的插入操作异常是指______。', '不该删除的数据被删除', '不该插入的数据被插入', '应该删除的数据未被删除', '应该插入的数据未被插入\n\n[解析] 关系规范化中的插入操作异常是指应该插入的数据未被插入。', 1, 'D', '无', '单选题', 50);
INSERT INTO `question` VALUES (977, '计算机主频的周期是指______。', '指令周期', '时钟周期', 'CPU周期', '存取周期', 1, 'B', '  计算机组成原理\n[解析] CPU的主频，即CPU内核工作的时钟频率(CPU Clock Speed)。通常所说的某某CPU是多少兆赫的，而这个多少兆赫就是“CPU的主频”。很多人认为CPU的主频就是其运行速度，其实不然。CPU的主频表示在CPU内数字脉冲信号震荡的速度，与CPU实际的运算能力并没有直接关系。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (978, '在请求分页存储管理系统中，若采用FIFO页面淘汰算法，则当分配的页面数增加时，缺页中断的次数______。', '减少', '增加', '无影响', '可能增加也可能减少', 1, 'D', '  操作系统\n[解析] 请求分页存储管理系统中，若采用FIFO页面淘汰算法，则当分配的页面数增加时，缺页中断的次数可能增加也可能减少。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (979, '作业调度程序的工作有______。', '分配和释放内存', '分配和释放外部设备', '把作业的内存、外设要求转给相应的管理程序', '对处理机的分配工作', 1, 'B', '  操作系统\n[解析] 作业调度程序的工作有分配和释放外部设备。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (980, '算法分析的目的是______。', '找出数据结构的合理性', '研究算法中输入和输出的关系', '分析算法的效率以求改进', '分析算法的易懂性和文档性\n\n[解析] 算法分析的目的是找出数据结构的合理性。', 1, 'A', '无', '单选题', 50);
INSERT INTO `question` VALUES (981, '在微机系统中，主机与高速硬盘进行数据交换一般用______方式。', '程序中断控制', 'DMA', '程序直接控制', '通道方式', 1, 'B', '  计算机组成原理\n[解析] DMA方式，Direct Memory Access，也称为成组数据传送方式，有时也称为直接内存操作。DMA方式的主要优点是速度快。由于CPU根本不参加传送操作，因此就省去了CPU取指令、取数、送数等操作。主机与高速硬盘进行数据交换，要求就是速度快，因此一般用DMA方式。\n  ', '单选题', 50);
INSERT INTO `question` VALUES (982, '下列说法中不正确的是______。', '任何可以由软件实现的操作也可以由硬件来实现', '固件就功能而言类似于软件，而从形态来说又类似于硬件', '在计算机系统的层次结构中，微程序级属于硬件级，其他四级都是软件级', '面向高级语言的机器是完全可以实现的', 1, 'D', '  计算机组成原理\n[解析] 硬件和软件的功能操作可以相互转换，硬件执行效率更高，但成本高，所以A项是对的。所谓固件就功能而言类似于软件，而从形态来说又类似于硬件，B是对的。微程序是由硬件直接实现的，是计算机系统最底层的硬件系统，由机器硬件直接执行微指令，C是对的。D项面向高级语言的机器是完全可以实现的说法过于绝对。\n  ', '单选题', 50);
