INSERT INTO `question` VALUES (1357, '在MySQL中，使用ALTER DATABASE命令时，如果数据库名被省略，表示修改当前（默认）数据库。', '', '', '', '', 3, '对', '在MySQL中，使用ALTER DATABASE命令时，如果数据库名被省略，表示修改当前（默认）数据库。', '判断题', 62);
INSERT INTO `question` VALUES (1358, '在MySQL中，将数据库db_smartfox中表tb_employe的字段salary 的默认值删除。则执行（）。', 'ALTER TABLE tb_employe CHANGE  salary  DROP DEFAULT;', 'ALTER TABLE tb_employe ALTER salary  DROP DEFAULT;', 'ALTER TABLE tb_employe MODIFY salary  DROP DEFAULT;', 'ALTER TABLE tb_employe ALTER salary  SET DEFAULT 4500;', 1, 'B', 'alter table tb_employe modify salary int;modify 能够删除默认约束 但 其本质是重新设置字段。<br>alter 是再不改变原属性基础上修改。\n', '单选题', 62);
INSERT INTO `question` VALUES (1359, 'SELECT语句的完整语法较复杂，但至少包括的部分是( )', '仅SELECT', 'SELECT，FROM', 'SELECT，GROUP', 'SELECT，INTO', 1, 'B', 'SELECT语句的完整语法较复杂，但至少包括的部分是SELECT和FROM。\n', '单选题', 62);
INSERT INTO `question` VALUES (1360, '有一表：DEPT (dno, dname) ,如果要找出倒数第三个字母为W，并且至少包含4个字母的dname，\n则查询条件子句应写成　WHERE dname LIKE ( )。', '\'_ _W_% \'', '\'_%W_ _\'', '\'_W_ \'', '\'_W_%\'', 1, 'B', '倒数第三个字母\n', '单选题', 62);
INSERT INTO `question` VALUES (1361, '视图是一个“虚表”，视图的构造基于( )', '基本表', '视图', '基本表或视图', '数据字典', 1, 'C', '视图是虚表,是从一个或几个基本表(或视图)中导出的表,在系统的数据字典中仅存放了视图的定义,不存放视图对应的数据。即真实的数据是存放在基本表中的\n', '单选题', 62);
INSERT INTO `question` VALUES (1362, '在MySQL中，需要修改 STUDENTS 表在STUDENT_ID列上增加一个PK约束，当前此表为空表，下面语句正确的是（  ）。', 'ALTER TABLE students ADD PRIMARY KEY student_id;', 'ALTER TABLE students ADD CONSTRAINT PRIMARY KEY (student_id);', 'ALTER TABLE students ADD CONSTRAINT stud_id_pk PRIMARY KEY student_id;', 'ALTER TABLE students ADD CONSTRAINT stud_id_pk PRIMARY KEY (student_id);', 1, 'D', '添加主键约束：\nALTER TABLE students ADD [CONSTRAINT <约束名>] PRIMARY KEY (主键字段名)；<br>正确语句：ALTER TABLE students ADD PRIMARY KEY （student_id）;ALTER TABLE students ADD CONSTRAINT stud_id_pk PRIMARY KEY （student_id）;\n', '单选题', 62);
INSERT INTO `question` VALUES (1363, '在MySQL中，执行如下语句：\nUPDATE 学生\nSET 午餐编号 = (SELECT 午餐编号 FROM 学生 WHERE 学生标识 = 17 )\nWHERE 学生标识 = 19;\n描述正确的是（）', '此语句将更新“学生”表，具体操作为将标识号为 19 的学生的午餐编号替换为标识号为 17 的学生的午餐编号。', '将一个新行插入“学生”表中。', '不发生任何变化，因为不能在 UPDATE的 SET语句中使用子查询。', '删除学生 17 的午餐编号，并根据学生 19 的午餐编号插入一个新值。', 1, 'C', 'mysql的update的一些特点1、update 时，更新的表不能在set和where中用于子查询；2、update 时，可以对多个表进行更新（sqlserver不行）；\n', '单选题', 62);
INSERT INTO `question` VALUES (1364, '在MySQL中，执行 SELECT COUNT(DISTINCT 薪金) FROM 雇员; 之后显示结果是（  ）', '雇员表中所有唯一薪金的列表', '雇员表中的薪金总额', '雇员表中的总行数', '雇员表中的唯一薪金的数量', 1, 'D', ' 在MySQL中，执行 SELECT COUNT(DISTINCT 薪金) FROM 雇员; 之后显示结果是雇员表中的唯一薪金的数量。DISTINCT 是去除重复，COUNT（）是统计行记录数。\n', '单选题', 62);
INSERT INTO `question` VALUES (1365, '在MySQL中，默认字符集latin1 下，数据“smartfox”存入varchar(20)和char(20)列中，存储空间分别是（  ）', '20字节，20字节', '8字节，20字节', '8字节，8字节', '9字节，20字节', 1, 'D', '用char(10)进行存储时，存储被补齐10个空格，其长度为10个字节，而<br>varchar的中存储的字符实际长度为4个字节。<br>注意 varchar实际 存储空间 =  字符实际长度+字段长度<br>如果是latin1 字符集时，空间=1N+（1 or 2）bytes，因为lantin1的1个字符占用1个字节，后面加的1或2bytes是用来表示字段长度的，如果超过255个字节，要2个bytes来表示字段长度；<br>如果是utf8字符集时，空间=3N+（1 or 3）bytes，因为utf8的1个字符占用3个字节，后面加的1或2bytes是用来表示字段长度的。当可能超过255字节时，要2个bytes来表示字段长度。\n', '单选题', 62);
INSERT INTO `question` VALUES (1366, '在MySQL中，默认字符集utf8下，数据“smartfox”存入varchar(20)和char(20)列中，存储空间分别是（  ）', '20,20', '25,60', '24,60', '25,20', 1, 'B', '用char(10)进行存储时，存储被补齐10个空格，其长度为10个字节，而varchar的中存储的字符实际长度为4个字节。<br>注意 varchar实际 存储空间 =  字符实际长度+字段长度<br>如果是latin1 字符集时，空间=1N+（1 or 2）bytes，因为latin1 的1个字符占用1个字节，后面加的1或2bytes是用来表示字段长度的，如果超过255个字节，要2个bytes来表示字段长度；<br>如果是gbk 字符集时，空间=1N+（1 or 2）bytes，因为latin1 的1个字符占用1个字节，后面加的1或2bytes是用来表示字段长度的，如果超过255个字节，要2个bytes来表示字段长度；<br>如果是utf8字符集时，空间=3N+（1 or 3）bytes，因为utf8的1个字符占用3个字节，后面加的1或2bytes是用来表示字段长度的。当可能超过255字节时，要2个bytes来表示字段长度。char 实际的存储空间大小也与字符集有关。utf8是3字节存储一个字符，所以20*3=60。\n', '单选题', 62);
INSERT INTO `question` VALUES (1367, '在 MySQL 中，有关获取表记录中年龄最大的三个人信息说法正确的是（）。', 'SELECT * FROM 表 ORDER BY 年龄 ASC LIMIT 0,3', 'SELECT * FROM 表 ORDER BY 年龄 DESC LIMIT 0,3', 'SELECT * FROM 表 ORDER BY 年龄 ASC LIMIT 1,3', 'SELECT * FROM 表 ORDER BY 年龄 DESC LIMIT 1,3', 1, 'B', '年龄最大的，需要将年龄 降序（ORDER BY 年龄  DESC）使用 LIMIT 0，3取前三个，即年龄最大的三个。\n', '单选题', 62);
INSERT INTO `question` VALUES (1368, '在 MySQL 中，使用函数TRUNCATE(2.99,1)则返回（）。', '2.9', '3', '2', '2.99', 1, 'A', 'TRUNCATE(x,y)函数用于返回x保留到小数点后y的值。注意不是四舍五入。\n', '单选题', 62);
INSERT INTO `question` VALUES (1369, '在MySQL中，在下列有关GROUP BY语句的描述中，不正确的是（ ）', '分组条件可以有多个，并且每一个可以分别指定排序方式', '可以使用WHERE子句对所得的分组进行筛选', 'GROUP BY可配合聚合函数一起使用，但GROUP BY子句中不能直接使用聚合函数', '除了聚合函数，SELECT语句中的每个列都必须在GROUP BY子句中给出', 1, 'B', 'GROUP BY对查询结果按字段列表进行分组，字段值相等的记录分为一组，指定用于分组的字段列表可以是一列，也可以是多个列，彼此间用逗号分隔，HAVING短语对分组的结果进行过滤，仅输出满足条件的组。答案B。\n', '单选题', 62);
INSERT INTO `question` VALUES (1370, '下列不是 BIOS 基本功能的是(　　)', '自检及初始化', '提供程序服务处理', '提供设备的驱动', '提供硬件中断处理', 1, 'C', 'BIOS的功能分为三个部分：自检及初始化，即主要负责启动电脑，包括用于电脑刚接通电源时对硬件部分的检测、初始化、引导程序；在启动阶段控制基本外设的输入输出操作(键盘、鼠标、磁盘读写、屏幕显示等);程序服务处理，即主要是为应用程序和操作系统服务，这些服务主要与输入输出设备有关，例如读磁盘、文件输出到打印机等；硬件中断处理，主要是分别处理PC机硬件的需求，BIOS的服务功能是通过调用中断服务程序来实现的，这些服务分为很多组，每组有一个专门的中断。\n', '单选题', 61);
INSERT INTO `question` VALUES (1371, '从数据传输方式来看，I/O接口可分为________。', '并行和串行', '低速和高速', '总线式和独占式', '标准接口和专用接口', 1, 'A', 'I/O接口分类从数据传输方式：（1）串行（数据传输方式）：一次传输1位数据；串行的传输速度不一定小于并行的传输速度.（COM接口，USB、IEEE-1394口）（2）并行（数据传输方式）：一次传输8位（或16位、32位）数据。(如LPT接口打印机或扫描仪、DVI、HDMI、VGA)从否需要 能连接多个设备为分：（1）独立式（独占式）：只能外接1个设备（DVI、HDMI等）（2）共享式（总线式）：可以扩展多个设备（USB等）', '单选题', 61);
INSERT INTO `question` VALUES (1372, 'Internt上的每一台计算机都有一个唯一的标识，即（）。', 'MAC地址', 'IP地址', '域名', 'URL', 1, 'B', 'Internt上的每一台计算机都有一个唯一的标识，即IP地址。\n', '单选题', 61);
INSERT INTO `question` VALUES (1373, '对于一个C类网络，要划分出3个子网，每个子网里有31台计算机，则子网掩码是（）', '255.255.255.0', '255.255.255.192', '255.255.255.224', '255.255.255.248', 1, 'B', 'C类默认网络段是3个字节，第4字节是用主机段。现要划分2个子网，需要2^n >= 3 则2^2= 4 > 2 所以至少需要2位。则最后一个字节的掩码序列为 11000000 即 192。所以划分后的子网掩码是 255.255.255.192。\n<Strong>C只能容纳30个主机地址</Strong>\n', '单选题', 61);
INSERT INTO `question` VALUES (1374, '下列URL表示中，正确的是_______。', 'ftp://ftp.fxedu.cn', 'http:www.qq.com', 'xiaoming@sina.com', 'www.shnu.edu.cn', 1, 'A', '统一资源定位器 URL①、标识 Web 中每个信息资源的位置。②、URL表示形式：【】表示可以没有<br><Strong>协议名://主机域名或IP 地址【:端口号】【/文件路径】/文件名</Strong><br>常用协议名：https、http、ftp等③、文件路径/文件如果不明确指出，则index.html或 default，html 为默认名，即网站的主页。\n', '单选题', 61);
INSERT INTO `question` VALUES (1375, '与10.110.12.29掩码255.255.255.224属于同一网段的主机IP地址是（）', '10.110.12.28', '10.110.12.30', '10.110.12.31', '10.110.12.32', 2, 'AB', '000·11101\n', '多选题', 61);
INSERT INTO `question` VALUES (1376, '网络地址:172.16.0.0，如果子网掩码为255.255.192.0，则以下说法正确的是（）', '其中一个子网的网络地址为： 172.16.68.0', '其中一个子网的广播地址为： 172.16.127.255', '其中一个子网的网络地址为： 172.16.128.0', '其中一个子网的广播地址为： 172.16.128.255', 2, 'BC', '172是B类的地址，默认网络段是16位，主机段也是16位。\n题目中255.255.192.0 ，192为 11000000，说明前两位用于子网划分。2位可以有4个子网：<br>00 ： 子网网络地址 是 172.16.0.0/18 广播地址 是 172.16.63.2555<br>01： 子网网络地址 是 172.16.64.0/18 广播地址 是 172.16.127.2555<br>10：子网网络地址 是 172.16.128.0/18 广播地址 是 172.16.191.255<br>11：子网网络地址 是 172.16.192.0/18 广播地址 是 172.16.255.255\n', '多选题', 61);
INSERT INTO `question` VALUES (1377, '常用的IP地址的分为A、B、C三类，可根据十进制IP地址第一个数字来判断，若第一数字在________范围，则表示属于A类IP地址。', '', '', '', '', 4, '(1) 1~126;1-126', '常用的IP地址的分为A、B、C三类，可根据十进制IP地址第一个数字来判断，若第一数字在1~126范围，则表示属于A类IP地址。注意0和127为特殊用处。\n', '填空题', 61);
INSERT INTO `question` VALUES (1378, '军事机构的域名是______。', '', '', '', '', 4, 'mil', '军事机构的域名是mil。\n', '填空题', 61);
INSERT INTO `question` VALUES (1379, '教育机构的域名是______。', '', '', '', '', 4, 'edu', '﻿教育机构的域名是edu。\n', '填空题', 61);
INSERT INTO `question` VALUES (1380, '每次访问，在地址栏中输入的域名，都会通过_____转换成IP地址。', '', '', '', '', 4, 'DNS', '﻿每次访问，在地址栏中输入的域名，都会通过DNS转换成IP地址。\n', '填空题', 61);
INSERT INTO `question` VALUES (1381, 'IP地址，全称为Internet Protocol地址，即网络协议地址。在网络中，根据TCP/IP协议，任何一台计算机都拥有一个IP地址，且这个IP地址必须唯一，以免在网络中造成冲突.', '', '', '', '', 3, '对', 'IP规定网络上所有的设备都必须有一个独一无二的IP地址，就好比是邮件上都必须注明收件人地址，邮递员才能将邮件送到。同理，每个IP信息包都必须包含有目的设备的IP地址，信息包才可以正确地送到目的地。同一设备不可以拥有多个IP地址，所有使用IP的网络设备至少有一个唯一的IP地址。', '判断题', 61);
INSERT INTO `question` VALUES (1382, '在因特网上的每台计算机都有自己的地址，该地址被称为域名。', '', '', '', '', 3, '错', '在因特网上的每台计算机都有自己的地址，该地址被称为IP地址。', '判断题', 61);
INSERT INTO `question` VALUES (1383, '扫描仪的主要技术指标不包括（  ）。', '分辨率', '扫描幅面', '扫描速度', '缓存容量', 1, 'D', '扫描仪的主要性能指标有：<strong>分辨率、灰度数、色彩数、扫描速度和扫描幅面</strong>。<br><b> 分辨率是扫描仪最主要的技术指标</b>，它表示扫描仪对图像细节上的表现能力，即决定了扫描仪所记录图像的细致度，其单位为DPI（用每英寸生成的像素数目）。通常用每英寸长度上扫描图像所含有像素点的个数来表示。 扫描速度有多种表示方法，因为扫描速度与分辨率，内存容量，软盘存取速度以及显示时间，图像大小有关，通常用指定的分辨率和图像尺寸下的扫描时间来表示。 扫描幅面：表示扫描图稿尺寸的大小，常见的有A4.A3.A0幅面等。\n', '单选题', 61);
INSERT INTO `question` VALUES (1384, '下列关于数码相机的叙述，错误的是________。', '数码相机采用CCD或CMOS芯片成像', '数码相机的存储容量越小，可存储的数字相片的数量就越少', '数码相机是一种图像输入设备', '数码相机的镜头和快门与传统相机基本相同', 1, 'B', '要的前提的，如果相同的分辨率的情况下，存储空间越小，存储的相片张数就越少。\n', '单选题', 61);
INSERT INTO `question` VALUES (1385, '下列计算机设备，既具备输入功能又具备输出功能的是_______。', '键盘', '鼠标', '条形码阅读器', '触摸屏', 1, 'D', '触摸屏可以显示也可以输入。\n', '单选题', 61);
INSERT INTO `question` VALUES (1386, '输入设备是向计算机输入数据和信息的设备。下列关于输入设备的描述中，错误的是________。', '条形码阅读器是用于读取条形码所包含的信息的一种设备', '触摸屏将输入和输出集中到一个设备上，简化了交互过程', '鼠标按照工作原理可分为两键鼠标和多键鼠标', '键盘上的功能键区各按键的作用是由软件定义的', 1, 'C', '鼠标按照工作原理可分为光电鼠标和机电鼠标。\n', '单选题', 61);
INSERT INTO `question` VALUES (1387, '鼠标器通常有两个按键，按键的动作会以电信号形式传送给主机，按键操作的作用主要由______决定。', '', '', '', '', 4, '正在运行的软件', '鼠标器通常有两个按键，按键的动作会以电信号形式传送给主机，按键操作的作用主要由正在运行的软件决定。\n', '填空题', 61);
INSERT INTO `question` VALUES (1388, '现在智能手机使用的越来越多，触摸屏已经是智能手机或设备的标配了，多点触摸又称__________，即一个屏幕允许多点操作，并能感应到手指滑动的快慢以及力度，从而使操控更加人性化。', '', '', '', '', 4, '多点触控', '现在智能手机使用的越来越多，触摸屏已经是智能手机或设备的标配了，多点触摸又称多点触控，即一个屏幕允许多点操作，并能感应到手指滑动的快慢以及力度，从而使操控更加人性化。\n', '填空题', 61);
INSERT INTO `question` VALUES (1389, '触摸屏由安装在显示器屏幕前面的检测部件和触摸屏控制器组成，所以它既是输入设备又是输出设备。', '', '', '', '', 3, '对', '触摸屏由安装在显示器屏幕前面的检测部件和触摸屏控制器组成，所以它既是输入设备又是输出设备。', '判断题', 61);
INSERT INTO `question` VALUES (1390, '所谓“云”计算就是一种计算平台或者应用模式。', '', '', '', '', 3, '错', '信息新技术\n[解析] 云计算(cloud computing)是基于互联网的相关服务的增加、使用和交付模式，通常涉及通过互联网来提供动态易扩展且经常是虚拟化的资源。<b>云是网络、互联网的一种比喻说法</b>。过去在图中往往用云来表示电信网，后来也用来表示互联网和底层基础设施的抽象。<b>狭义云计算指IT基础设施的交付和使用模式，指通过网络以按需、易扩展的方式获得所需资源；广义云计算指服务的交付和使用模式，指通过网络以按需、易扩展的方式获得所需服务</b>。这种服务可以是IT和软件、互联网相关，也可是其他服务。它意味着计算能力也可作为一种商品通过互联网进行流通。', '判断题', 61);
INSERT INTO `question` VALUES (1391, '“云”计算服务可信性依赖于计算平台的安全性。', '', '', '', '', 3, '错', '信息新技术\n[解析] 服务提供商可信是指：他向其他服务商或者用户提供的服务必须是可信的，<b>而不是恶意的或者其他服务提供商或用户不需要的信息</b>；用户可信是指用户采用正常的合法的方式访问服务提供商提供的服务，<b>用户的行为不会对服务提供商本身造成破坏。</b>', '判断题', 61);
INSERT INTO `question` VALUES (1392, 'Raid1是备份量极高的Raid策略，相应的它的保护能力也很强。', '', '', '', '', 3, '错', '信息新技术\n[解析] raid1其实是就<b>用一组硬盘对另外一组硬盘进行备份的过程，将会产生一份冗余的数据以防止意外情况的发生</b>。raid1就是同时对2个硬盘读写(同样的数据)。<b>强调数据的安全性。比较浪费</b>。同样也是那两块硬盘，如果组成了R1的话显示的容量只是一个硬盘的容量，R1也是把数据分成了两份，分别存在了两块磁盘中，两个硬盘理论上可以分成四份，一个硬盘的一半是数据区，另一半是另一块硬盘数据区的备份区，R1有可能性能不会提升反而下降。', '判断题', 61);
INSERT INTO `question` VALUES (1393, '云计算作为中国移动蓝海战略的一个重要部分，于2007年由移动研究院组织力量，联合中科院计算所，着手起步了一个叫做______的项目。', '“国家云”', '“蓝天”', '“蓝云”', '“大云”', 1, 'D', ' 信息新技术\n[解析] 云计算作为中国移动蓝海战略的一个重要部分，于<b>2007年由移动研究院组织力量，联合中科院计算所</b>，着手起步了一个叫做“<b>大云</b>”的项目。蓝云是IBM推出的计算平台。\n', '单选题', 61);
INSERT INTO `question` VALUES (1394, '______当数最大的云计算的使用者。', 'Salesforce', 'Microsoft', 'Giwell', 'Google', 1, 'D', ' 信息新技术\n[解析] Google Google当数最大的云计算的使用者。Google搜索引擎就建立在分布在200多个地点、超过100万台服务器的支撑之上，这些设施的数量正在迅猛增长。\n', '单选题', 61);
INSERT INTO `question` VALUES (1395, '一个有10个硬盘组成的Raid5阵列最多可以允许______硬盘出现故障不影响其数据的完整性。', '2个', '3个', '4个', '1个', 1, 'D', ' 信息新技术\n[解析] Raid5需要3块硬盘，Raid5把数据和相对应的奇偶校验信息存储到组成Raid5的各个磁盘上，并且奇偶校验信息和相对应的数据分别存储于不同的磁盘上，<b>其中任意N-1块磁盘上都存储完整的数据</b>，也就是说有相当于一块磁盘容量的空间用于存储奇偶校验信息。因此当Raid5的一个磁盘发生损坏后，不会影响数据的完整性，从而保证了数据安全。<b>当损坏的磁盘被替换后，Raid还会自动利用剩下奇偶校验信息去重建此磁盘上的数据，来保持Raid5的高可靠性</b>。当更换故障硬盘后，在故障期间写入的数据会进行重新校验。如果在未解决故障又坏1块，那就是灾难性的了。\n', '单选题', 61);
INSERT INTO `question` VALUES (1396, '微软于2008年10月推出云计算操作系统是______。', 'Azure', 'EC2', 'Google App Engine', '蓝云', 1, 'A', ' 信息新技术\n[解析] 微软于2008年10月推出云计算操作系统是Windows Azure platform。\n亚马逊弹性云计算（英语：Amazon Elastic Compute Cloud，简称Amazon EC2） ，是由亚马逊公司提供的Web服务，是一个让用户可以租用云端电脑<i>运行所需应用<i/>的系统。\n', '单选题', 61);
INSERT INTO `question` VALUES (1397, '______年中国把物联网发展写入了政府工作报告。', '2009', '2010', '2000', '2008', 1, 'B', ' 信息新技术\n[解析] 2010年3月5日，温家宝总理在作政府工作报告时指出，要大力发展新能源、新材料、节能环保、生物医药、信息网络和高端制造产业；积极推进新能源汽车、“三网”融合取得实质性进展，加快物联网的研发应用；加大对战略性新兴产业的投入和政策支持。这是首次将物联网写入政府工作报告中。\n', '单选题', 61);
INSERT INTO `question` VALUES (1398, '迄今为止最经济实用的一种自动识别技术是______。', '条形码识别技术', '语音识别技术', '生物识别技术', 'IC卡识别技术', 1, 'A', ' 信息新技术\n[解析] 迄今为止最经济实用的自动识别技术是条形码识别技术。条码技术广泛应用于商业、邮政、图书管理、仓储、工业生产过程控制、交通等领域。\n', '单选题', 61);
INSERT INTO `question` VALUES (1399, '下列哪项不是物联网的数据管理系统结构______。', '星形式结构', '层次式结构', '集中式结构', '分布式结构和半分布式结构', 1, 'A', ' 信息新技术\n[解析] 物联网数据管理系统结构主要有层次式结构、集中式结构、分布式结构、半分布式结构。\n \n', '单选题', 61);
INSERT INTO `question` VALUES (1400, '在智慧医疗技术通过物联网技术向物理世界延伸的过程中，______技术起到了桥梁性的作用。', '技术支撑层技术', '应用接口层', '医疗信息感知技术', '物联网接入层技术', 1, 'A', ' 信息新技术\n[解析] 在智慧医疗技术通过物联网技术向物理世界延伸的过程中，技术支撑层技术起到了桥梁性的作用。', '单选题', 61);
INSERT INTO `question` VALUES (1401, '进程与线程的根本区别是______。', '进程要占用一个进程控制块，开销较大，而线程无此开销', '进程是资源分配单位，而线程是调度和执行的单位', '进程增加会增加死锁的机会，而线程有效避免了这一问题', '进程是调度和执行单位，而线程是资源分配单位', 1, 'B', '操作系统\n[解析] 线程是进程中的一个实体，是被系统独立调度和分派的基本单位，线程自己不需有系统资源，只需基本的一点点资源。\n', '单选题', 61);
INSERT INTO `question` VALUES (1402, '磁表面存储器记录信息是利用磁性材料的______。', '磁滞回归线特性', '磁场渗透特性', '磁场分布特性', '磁场吸引力特性', 1, 'A', '计算机组成原理\n[解析] 磁表面存储器记录信息是利用磁性材料的磁滞回归线特性。<br>即当外加磁场施加于铁磁质时，其原子的偶极子按照外加场自行排列。即使当外加场被撤离，部分排列仍保持：\n', '单选题', 61);
INSERT INTO `question` VALUES (1403, '数据的最小单位是______。', '数据项', '数据类型', '数据元素', '数据变量', 1, 'A', '数据的最小单位是数据项。<br>基本单位是数据元素</br>\n', '单选题', 61);
INSERT INTO `question` VALUES (1404, '芯片组的主要生产厂家有______。', 'VIA公司', 'SiS公司', 'ALi公司', 'Intel公司', 2, 'ABCD', ' 威盛电子的客户群涵盖全球各大OEM厂商、主机板制造业者及系统整合业者，总部则位于台湾台北县新店市。<br>\n矽统科技股份有限公司（英语：Silicon Integrated Systems，常缩写为SiS）是一家芯片设计公司，1987年8月26日成立，坐落于台湾新竹科学园区。<br>\n阿里巴巴成立新公司！经营范围包括集成电路 - 电子工程\n', '多选题', 61);
INSERT INTO `question` VALUES (1405, '下面浮点运算器的描述中正确的句子是______。', '浮点运算器可用阶码部件和尾数部件实现', '阶码部件可实现加、减、乘、除四种运算', '阶码部件只进行阶码相加、相减和比较操作', '尾数部件只进行乘法和减法运算', 2, 'AC', '  浮点运算可用两个松散连接的定点运算部件来实现：即阶码部件和尾数部件，浮点运算器的一般结构尾数部件实质上就是一个通用的定点运算器，要求该运算器能实现加、减、乘、除四种基本算术运算。阶码部件只进行阶码相加、相减和比较操作。\n', '多选题', 61);
INSERT INTO `question` VALUES (1406, '计算机网络按照覆盖地域大小可分为______。', '无线网路', '局域网', '有线网路', '广域网', 2, 'BD', 'AC是按照传输介质分类。\n', '多选题', 61);
INSERT INTO `question` VALUES (1407, '一个网络可以由两台计算机组成，也可以由在同一大楼里面的上千台计算机组成。我们通常把这样的网络称为局域网(LAN)，由LAN再延伸出去形成更大的范围，比如整个城市，这样的网络我们称之为广域网(WAN)。一个计算机网络的主要组成部分包括______。', '若干主机和终端', '数据库、文件服务器', '电话网、电话线、网络连接线', '一个通信子网、一系列通信协议', 2, 'AD', '计算机网络的三个主要组成部分是：若干主机、一个通信子网、一系列通信协议。<br>\n1、若干个主机，它们向各用户提供服务；<br>\n\n2、一个通信子网，它由一些专用的结点交换机和连接这些结点的通信链路所组成；<br>\n\n3、一系列的协议；这些协议是为在主机之间或主机和子网之间的通信而用的。\n', '多选题', 61);
INSERT INTO `question` VALUES (1408, '设计实时操作系统必须首先考虑系统的______。', '实时性', '效率', '可靠性', '可移植性', 2, 'AC', ' 实时操作系统(RTOS)是指当外界事件或数据产生时，能够接受并以足够快的速度予以处理，其处理的结果又能在规定的时间内来控制生产过程或对处理系统做出快速响应，调度一切可利用的资源完成实时任务，并控制所有实时任务协调一致运行的操作系统。提供及时响应和高可靠性是其主要特点。\n', '多选题', 61);
INSERT INTO `question` VALUES (1409, '同步传输之所以比异步传输具有较高的传输频率是因为同步传输______。', '用一个公共时钟信号进行同步', '各部件存取时间较为接近', '不需要应答信号', '总线长度较短', 2, 'AC', '同步传输是一种以数据块为传输单位的数据传输方式，该方式下数据块与数据块之间的时间间隔是固定的，必须严格地规定它们的时间关系。每个数据块的头部和尾部都要附加一个特殊的字符或比特序列，标记一个数据块的开始和结束，一般还要附加一个校验序列，以便对数据块进行差错控制。异步传输(Asynchronous Transmission)：异步传输将比特分成小组进行传送，小组可以是8位的1个字符或更长。发送方可以在任何时刻发送这些比特组，而接收方从不知道它们会在什么时候到达。\n', '多选题', 61);
INSERT INTO `question` VALUES (1410, '下列说法中，错误的是______。', '服务器不能用个人计算机构成', '服务器只能用大型主机、小型机构成', '服务器只能用装配有安腾处理器的计算机构成', '服务器可以用装配有奔腾、安腾处理器的计算机构成', 2, 'ABC', '从广义上讲，服务器是指网络中能对其他机器提供某些服务的计算机系统(如果一个PC对外提供ftp服务，也可以叫服务器)；从狭义上来讲，服务器是专指某些高性能计算机，能够通过网络，对外提供服务；服务器的构成与微机基本相似，有处理器、硬盘、内存、系统总线等。', '多选题', 61);
INSERT INTO `question` VALUES (1411, '在数据库中，产生数据不一致的根本原因是______。', '未对数据进行完整性控制', '没有严格保护数据', '数据存储量太大', '数据冗余', 1, 'D', '在数据库中，产生数据不一致的根本原因是数据冗余。\n', '单选题', 61);
INSERT INTO `question` VALUES (1412, '下列关于网络互连设备的正确描述是______', '网关是数据链路层的互连设备', '中继器和网桥都具备纠错功能', '路由器和网关都具备协议转换功能', '网桥不具备路由选择功能', 1, 'C', '路由器和网关都是在网络层的设备，根据不同协议进行转换。<br>\n三层交换机具备路由选择功能\n', '单选题', 61);
INSERT INTO `question` VALUES (1413, '路由器作为网络互联设备，其主要的特点可以概括为______。', '选择最优路由', '可以按MAC地址对数据包进行转发', '在不同的网段或网络之间转发数据包', '采用分组交换方式、将数据包分成较小的包，到达目的地以后再组装起来\nE.支持多协议操作的路由器，可以连接使用不同的通信协议的网段或计算机网络\nE', 2, 'AC', '路由器是网络层设备，主要功能是完成路径的选择。支持不同协议之间的路由操作，可以完成异构网络的互联工作。\n', '多选题', 61);
INSERT INTO `question` VALUES (1414, '下列不属于数组的主要操作的是______。', '检索(查找)', '修改', '插入', '删除\nE.存取', 2, 'CD', '数数组的基本操作有存取、修改、检索和排序等，没有插入与删除操作\n', '多选题', 61);
INSERT INTO `question` VALUES (1415, '硬盘按接口类型可以分为______两种。', 'ATA接口的硬盘', 'ISA接口的硬盘', 'IDE接口的硬盘', 'SCSI接口的硬盘', 2, 'CD', '硬盘从接口类型上可以分为以下几种：IDE接口、SATA接口、SCSI接口、光纤通道接口、SAS接口、PCI-E接口。至于其他的比如mSATA、eSATA、USB等都是转换出来的接口类型。ATA并行很少使用；\n', '多选题', 61);
INSERT INTO `question` VALUES (1416, '电脑系统的显示系统包括______。', '显卡', '显示器', '显示内存', '3D图形加速卡', 2, 'AB', '显卡是完成数字信号和模拟信号的转化，用于连接显示器，显卡的好坏决定了显示质量。显示器是计算机的输出工具。显示内存是显卡的一个内存储器。\n \n', '多选题', 61);
INSERT INTO `question` VALUES (1417, '线性表的顺序存储结构是一种______的存储结构。', '散列存取', '顺序存取', '索引存取', '随机存取', 2, 'BD', '线性表的顺序存储结构是一种顺序存储的存储结构，顺序存储结构又称为随机存取结构。\n', '多选题', 61);
INSERT INTO `question` VALUES (1418, '在交互控制方式下，用户可以采用______来控制作业的执行。', '汇编语言', '作业控制语言', '命令语言', '会话语言', 2, 'CD', '汇编语言完成的是高级语言向机器语言的转化过程，作业控制语句是计算机操作员或计算机自动执行的过程。\n', '多选题', 61);
INSERT INTO `question` VALUES (1419, '声卡最主要的组成部分为______。', '功率放大器', '总线接口、输入/输出端口', '声音处理芯片', 'MIDI及游戏杆接口、CD音频连接器', 2, 'ABCD', '声卡的组成：①数字信号处理芯片：数字信号处理芯片可以完成各种信号的记录和播放任务，还可以完成许多处理工作，如音频压缩与解压缩运算、改变采样频率、解释MIDI指令或符号以及控制和协调直接存储器访问(DMA)工作。②A/D和D/A转换器：声音原本以模拟波形的形式出现，必须转换成数字形式才能在计算机中使用。为实现这种转换，声音卡含有把模拟信号转成数字信号的A/D转换器，使数据可存入磁盘中。为了把声音输出信号送给喇叭或其他设备播出，声卡必须使用D/A转换器，把计算机中以数字形式表示的声音转变成模拟信号播出。③总线接口芯片：总线接口芯片在声卡与系统总线之间传输命令与数据。④音乐合成器：音乐合成器负责将数字音频波形数据或MIDI消息合成为声音。⑤混音器：混音器可以将不同途径，如话筒或线路输入、CD输入的声音信号进行混合。此外，混音器还为用户提供软件控制音量的功能。', '多选题', 61);
INSERT INTO `question` VALUES (1420, '显卡与主板连接的接口主要有______。', 'ISA接口', 'PCI接口', 'AGP接口', 'VESA接口', 2, 'ABCD', '计算机组成原理\n', '多选题', 61);
INSERT INTO `question` VALUES (1421, '硬盘的性能指标包括______。', '转速', '平均搜索时间', '数据传输率', '数据缓存', 2, 'ABCD', ' 转速是指硬盘一分钟转动的圈数，一般是5400转/分；7200转/分等。平均搜索时间：从开始查找数据到找到数据所需要的时间；单碟容量：一块硬盘所能容纳的数据量，在一些磁盘中可能有多个盘片；数据传输率：硬盘内部传输速率和外部传输速率；数据缓存：缓解硬盘和内存的速度差，一般为8MB、16MB、32MB等。\n', '多选题', 61);
INSERT INTO `question` VALUES (1422, 'Windows 98是一个______的操作系统。', '具有即插即用功能', '具有MS-DOS兼容性', '不依赖于MS-DOS的完全独立的', '能同时运行多个应用程序', 2, 'ABCD', 'Windows是一个多任务的操作系统，本身把DOS也兼容进了windows系统中。在USB使用上是即插即用的。\n', '多选题', 61);
INSERT INTO `question` VALUES (1423, '下列哪一条不是顺序存储结构的优点______。', '存储密度大', '插入运算方便', '删除运算方便', '可方便的用于各种逻辑结构的存储表示', 2, 'BCD', '顺序存储不便于频繁的进行插入和删除操作。\n', '多选题', 61);
INSERT INTO `question` VALUES (1424, '下列关于计算机网络协议的表述准确的是______。', '网络协议是计算机网络中互相通信的对等实体间交换信息时所必须遵守的规则的集合', '网络协议主要由语言、语法、载体三要素构成', '网络协议属于计算机网络软件系统', '网络协议是网络软件系统中最重要、最核心的部分', 2, 'ACD', '网络协议是由语法、语义、时序组成。<br>\n网络协议软件网络协议是网络通信的数据传输规范，网络协议软件是用于实现网络协议功能的软件\n \n', '多选题', 61);
INSERT INTO `question` VALUES (1425, '要求以作业形式提交计算机计算的系统有______。', '信息查询系统', '批处理系统', '事务处理系统', '分时系统', 2, 'BD', '批处理操作系统是把计算机要处理的内容一次性交给计算机，这样必须构成一次作业。分时操作系统是把CPU分成不同的时间片交给不同的用户控制，这样用户每次操作CPU可以称是一次作业。<br>其它都不是操作系统\n', '多选题', 61);
INSERT INTO `question` VALUES (1426, '主键、超键、候选键的关系是______。', '主键一定是超键，候选键一定是超键', '主键一定是超键，候选键不一定是超键', '候选键一定是主键，候选键不一定是超键', '超键不一定是主键，候选键一定是超键', 2, 'AD', '超键(super key)：在关系中能惟一标识元组的<b>属性集</b>称为关系模式的超键。候选键(candidate key)：不含有多余属性的超键称为候选键。主键(primary key)：用户选作元组标识的一个候选键程序主键。主键一定是候选键，候选键一定是超键。', '多选题', 61);
INSERT INTO `question` VALUES (1427, '在同样大小的显示器屏幕上，显示分辨率越大，则屏幕显示的文字。（）', '越小', '越大', '大小不变', '字体增大', 1, 'A', '在操作系统中为显示器设置两种不同分辨率时，桌面的图标文字等都有变化，分辨率越高时，图标就会小。因为每一个像素点变大了\n', '单选题', 61);
INSERT INTO `question` VALUES (1428, 'LCD显示器在显示动画时有拖尾现象，说明哪个指标比较低（）.', '高度', '对比度', '响应时间', '刷新率', 1, 'C', '响应时间：响应时间越小越好，越小时显示器看速动画面时不会出现尾影拖拽的现象\n', '单选题', 61);
INSERT INTO `question` VALUES (1429, '下列最先进的显示器是（）显示器。', 'LED', 'LCD', '等离子', 'CRT', 1, 'C', 'PDP的全称是Plasma Display Panel，中文叫等离子体显示器，是一种显示器，与传统的CRT显像管结构相比，具有分辨率高，屏幕大，超薄，色彩丰富、鲜艳的特点。确实先进但再营销策略上败了下来，所以不被大众买单。\n', '单选题', 61);
INSERT INTO `question` VALUES (1430, '（）是直接由电子计算机或数字信号控制，用以自动输出各种图形、图像和字符的绘图设备。', '投影仪', '绘图仪', '显示器', '打印机', 1, 'B', '绘图仪是直接由电子计算机或数字信号控制，用以自动输出各种图形、图像和字符的绘图设备。\n', '单选题', 61);
INSERT INTO `question` VALUES (1431, '打印机在与主机连接时，可将信号线插头插在( )。', '串行接口上', '并行接口上', '串/并行接口上', '任意扩展接口上', 1, 'C', '打印机与主机连接时，主要是串行、并行、USB、SCSI、IEEE1394等。\n', '单选题', 61);
INSERT INTO `question` VALUES (1432, '下列打印机中，打印效果最佳的一种是( )。', '点阵打印机', '激光打印机', '热敏打印机', '喷墨打印机', 1, 'D', '喷墨输出设备分辨率比激光输出设备佳，可印制高解析的影像，但分辨率要求愈高，就需要较专业的纸张，如喷墨专用纸、相片纸，才可顺利印制、效果不出现毛边。\n', '单选题', 61);
INSERT INTO `question` VALUES (1433, '下列关于液晶显示器的叙述，正确的是（  ）', '它与CRT显示不同，不需要使用显示卡', '显示画面的亮度取决于液晶自身的亮度', '响应时间越长越好', '显示方式有反射型、透射型及反射透射结合型等', 1, 'D', '选项A  需要显示卡。\n选项B 液晶显示器通常会使用一到两个大型萤光灯或是LED当作其背光源，在背光源上面的液晶面板则是利用遮罩的原理让显示器显示出不同颜<br>选项C 响应时间越小越好选项 D 显示方式有反射型、透射型及反射透射结合型(半透射型)。\n', '单选题', 61);
INSERT INTO `question` VALUES (1434, '对输入输出设备，通常输入输出操作的信息传输单位为(  ）', '位', '字节', '字', '块', 1, 'B', '﻿对输入输出设备，通常设备输入输出操作的信息传输单位为字节。\n', '单选题', 61);
INSERT INTO `question` VALUES (1435, '计算机的外围设备可分______和______ 两 大类。', '', '', '', '', 4, '外存;I/O设备', '﻿计算机的外围设备可分外存和I/O设备 两大类。\n', '填空题', 61);
INSERT INTO `question` VALUES (1436, '显示器是由监视器与显示卡两部分组成的。', '', '', '', '', 3, '对', '计算机必不可少的图文输出设备，它能将数字信号转化为光信号，使文字和图像在屏幕上显示出来。组成：显示器（监视器）和显示控制器（显卡，显示卡）', '判断题', 61);
INSERT INTO `question` VALUES (1437, '显示器的扫描方式有逐行扫描和隔行扫描两种，隔行扫描指的是先扫描2，4，6，8等偶数行信号，再扫描1，3，5，7等奇数行信号。', '', '', '', '', 3, '错', '先奇后偶', '判断题', 61);
INSERT INTO `question` VALUES (1438, '计算机的外围设备是指（  ）', '输入/输出设备', '外存', '远程通信设备', '除了CPU和内存以外的其他设备', 1, 'D', '外围设备即计算机系统中<b>除主机外</b>的其他设备。 包括输入和输出设备、外存储器、模数转换器、数模转换器、外围处理机等。 是计算机与外界进行通信的工具。\n', '单选题', 63);
INSERT INTO `question` VALUES (1439, '以下有关软件按照权益的处置方式分类的描述，不正确的是（  ）', '商品软件是指用户需要付费才能得到其使用权，Winodws 10操作系统就属于商品软件。', '自由软件即在协议过下用户可共享软件，允许随意拷贝和修改其源代码，允许自由传播。', '共享软件,是一种免费使用具有版权的软件。', '开源软件指在软件发行的时候必须附上完整的源代码，并在协议规定下用户可以修改和再发布。', 1, 'C', '共享软件是一种“买前免费试用”的具有版权的软件，它通常允许用户试用一段时间，也允许用户进行拷贝和散发（但是不可以修改后散发）asdasdas\n', '单选题', 63);
INSERT INTO `question` VALUES (1440, '设某棵二叉树中有2000个节点，则该二叉树的最小深度为（  ）', '10', '11', '12', '13', 1, 'B', ' log2(2000)+ 1=10+1 =11<br>2^11 = 2048>2000<br>2^10= 1024 < 2000\n', '单选题', 63);
INSERT INTO `question` VALUES (1441, '下列不属于大数据结构类型的是（  ）', '非结构化数据', '结构化数据', '半结构化数据', '锚点数据', 1, 'D', '大数据结构类型包括结构化、半结构化和非结构化。非结构化数据是主要部分。\n\n', '单选题', 63);
INSERT INTO `question` VALUES (1442, 'Amazon EC2是一个让使用者可以租用云端计算机运行所需应用系统，借由提供Web服务的方式提供可调整的云计算能力，使用者将可以在这个虚拟机运行任何自己想要的软件或应用程序。这个平台属于云计算应用模式（  ）', 'IaaS', 'PaaS', 'SaaS', 'DaaS', 1, 'A', 'Amazon EC2是典型的IaaS（基础即服务）应用模式。除了它还有OpenStack平台。\n', '单选题', 63);
INSERT INTO `question` VALUES (1443, '（   ）的最终研究目标就是使计算机能像人那样通过视觉观察和理解世界，具有自主适应环境的能力。', '图像分析', '计算机视觉', '图像识别', '机器学习', 1, 'B', '计算机视觉就是是指用摄影机和电脑代替人眼对目标进行识别、跟踪和测量等机器视觉，并进一步做图形处理，使电脑处理成为更适合人眼观察或传送给仪器检测的图像。\n', '单选题', 63);
INSERT INTO `question` VALUES (1444, '通常区块链系统由（）组成。', '数据层', '网络导', '合约层', '传输层', 2, 'ABC', '一般说来，区块链系统由数据层、网络层、共识层、激励层、合约层和应用层组成。\n', '多选题', 63);
INSERT INTO `question` VALUES (1445, '__________是运算计算机、网络和通信等现代信息技术手段实现政府组织结构和工作流程的优化和重组。', '', '', '', '', 4, '电子政务', '电子政务是运算计算机、网络和通信等现代信息技术手段实现政府组织结构和工作流程的优化和重组。\n', '填空题', 63);
INSERT INTO `question` VALUES (1446, '在互联网上，可以通过网页来获得资源，是一种资源共享方式，信息通常以__________方式进行组织。', '', '', '', '', 4, '超文本', '在互联网上，可以通过网页来获得资源，是一种资源共享方式，信息通常以超文本方式进行组织。\n', '填空题', 63);
INSERT INTO `question` VALUES (1447, '计算机键盘上盲打的两个定位键的，将两个大写英文字母的ASCII码数值进行算术加法后结果使用十六进制表示是____________.', '', '', '', '', 4, '90', '为了方便盲打，键盘上有两个定位，分别是“F“键和”J“键\'F\' = 70 = 46H\'J\'  = 74 = 4AH46H+4AH = 90H \n', '填空题', 63);
INSERT INTO `question` VALUES (1448, 'GIF是目前互联网上广泛使用的一种图像文件格式，它的颜色数目超过256色，数据特别小，适合于互联网传输。', '', '', '', '', 3, '错', 'GIF是目前互联网上广泛使用的一种图像文件格式，它的颜色数目不超过256色，数据特别小，适合于互联网传输。 GIF（无损压缩）。①、GIF图像格式颜色数目不超过256色（色彩深度8位），适合用作插画、剪贴画等色彩要求不高的场合，文件特点小，适合网络传输。②、GIF图像格式具有累进显示功能，适合网络浏览器观看。③、GIF图像格式能支持透明背景（JPEG不支持）。④、GIF图像格式能支持动画。⑤、不适合真彩色图片⑥、文件扩展名：.gif', '判断题', 63);
INSERT INTO `question` VALUES (1449, '实现分组交换方式的数据传输，网络中必须使用一种关键设备是分组交换机，交换机与交换机之间是高速数字通信线路，如光纤、微波、无线频道等。', '', '', '', '', 3, '对', '实现分组交换方式的数据传输，网络中必须使用一种关键设备是分组交换机，交换机与交换机之间是高速数字通信线路，如光纤、微波、无线频道等。', '判断题', 63);
INSERT INTO `question` VALUES (1450, '有以下程序：\n<pre>\n#include <stdio.h>\n\nstatic int n =0;\nint fun(int m){\n\n     n+=m;\n\n    return n;\n\n}\n\nmain(){\n\n    int n =0;\n\n    n=fun(++n);\n\n    n= fun(n++);\n\n    printf(\"%d\\n\",++n);\n\n}</pre>', '0', '2', '3', '4', 1, 'C', '无', '单选题', 63);
INSERT INTO `question` VALUES (1451, '若有一个病人表R（住院号，姓名，性别，科室号，病房，家庭住址），\n“住院号”唯一标识R表的每一个元组，\n\n“性别”只能为M或F\n\n“家庭住址”包括省、市、街道、邮编。\n<pre>\n以下是创建表R的SQL语句：\n\nCREATE TABLE  R（\n\n    住院号 CHAR(8) ___________,\n\n    姓名 CHAR (25)\n\n    性别 CHAR(1) ___________,\n\n    科室号  CHAR(4) ，\n\n    家庭住址 ADDR，//ADDR 为用户定义的类\n\n);</pre>', 'NOT NULL， PRIMARY KEY', 'PRIMARY KEY, CHECK(\'M\',\'F\')', 'FOREIGN KEY （科室号），IN（‘M’,\'F\'）', 'PRIMARY KEY, CHECK（性别  IN （\'M\',\'F\'）)', 1, 'D', '住院号是主键，PRIMARY KEY“性别”只能为M或F，需要列检查约束CHECK（性别  IN （\'M\',\'F\'）)\n', '单选题', 63);
INSERT INTO `question` VALUES (1452, '有以下程序：\n#include <stdio.h>\n<pre>\nmain(){\n\n    int a=-2,b=0;\n\n     while(a++&&++b);printf(\"%d,%d\\n\",a,b);\n\n}</pre>\n\n则程序运行后的输出结果是______', '', '', '', '', 4, '1,2', '注意循环体是空语句哦。\n', '填空题', 63);
INSERT INTO `question` VALUES (1453, '在Linux中，可以使用__________  ens33  down/up 来启用或禁用网络接口，用它也可以用来查看网卡配置信息。', '', '', '', '', 4, ' ifconfig', 'ifconfig 命令是一个可以用来查查看、配置、启用或禁用网络接口的命令。ifconfig [网络设备] [选项]down 禁用网卡up 启用网卡\n', '填空题', 63);
INSERT INTO `question` VALUES (1454, '一个循环的循环体中套有另一个循环称为循环嵌套定义，一个循环外面仅包围一层循环称为循环嵌套调用。', '', '', '', '', 3, '错', '一个循环的循环体中套有另一个循环称为循环嵌套，一个循环外面仅包围一层循环称为双层循环。', '判断题', 63);
INSERT INTO `question` VALUES (1455, '【综合题】\n设有整数数列a0,a1,a2,...，an-1，给出求解最大值的递归程序。请补全代码。\n\nMaxValue函数的功能就是返回数组中最大值。\n\n例如：整数数列是 12,44,1,5,8,99,23,20,33,76，返回结果是99\n\n代码如下：\n<pre>\n#include  <stdio.h>\n\nint MaxValue(int a[],int n){\n\n	int max;\n\n	if(n==0)\n\n		max = a[0];\n\n	else if( a[n]> MaxValue(a,n-1))\n\n		_______________;\n\n	else \n\n              ___________;\n\n      return  max;\n\n}\n\nmain(){\n\n	int aa[10] = { 12,44,1,5,8,99,23,20,33,76};\n\n	int  max = MaxValue(aa,9);\n\n	printf(\"max = %d\\n\",max);\n\n	system(\"pause\");\n\n}\n</pre>', '', '', '', '', 4, 'max = a[n];max = MaxValue(a,n-1)', '无', '填空题', 63);
INSERT INTO `question` VALUES (1456, '有以下程序：\n<pre>\n#include <stdio.h>\nmain(){\n\nint a=0, b=1,c=-1;\n\n    if(a) b++;\n\n    else if(a==0) if(b==0)c++; \n\n	else c++;\n\n	printf(\"%d\\n\",~c);\n\n	system(\"pause\");\n\n    printf(\"\\n\");\n\n}\n</pre>\n则程序运行后输出的结果（  ）', '0', '1', '-1', '2', 1, 'C', '\n最终 c=0，取反之后-1', '单选题', 63);
INSERT INTO `question` VALUES (1457, '在软件开发的瀑布模型中，应该在（）阶段制定单元测试计划。', '需求分析', '概要设计', '详细设计', '代码编写', 1, 'C', '根据测试的目的、阶段的不同，可以把测试分为单元测试、集成测试、确认测试和系统测试等几类。在瀑布模型中，单元测试的计划应该在详细设计阶段制定，集成测试计划在概要设计阶段指定，确认测试在需求分析阶段制定，系统测试计划系统分析阶段制定。但是，在v模型中，应该在代码编写阶段制定单元测试计划，在软件详细设计中制定集成测试计划，在概要设计中制定系统测试计划。\n', '单选题', 63);
INSERT INTO `question` VALUES (1458, '检查软件产品是否符合需求定义的过程称为 （） 。', '确认测试', '集成测试', '验证测试', '验收测试', 1, 'A', '确认测试是对通过组合测试的软件进行的，这些软件已经存于系统目标设备的介质上。确认测试的目的是要表明软件是可以工作的，并且符合”软件需求说明书”中规定的全部功能和性能要求。<br>\nA、C、D相近，但A为比较正规的说法名词。\n', '单选题', 63);
INSERT INTO `question` VALUES (1459, '在结构测试用例设计中，有语句覆盖、判定覆盖、路径覆盖等，其中(   )是最强的覆盖准则 。', '语句覆盖', '条件覆盖', '判定覆盖', '路径覆盖', 1, 'D', '逻辑覆盖：语句覆盖、判定覆盖、条件覆盖、判定/条件覆盖、条件组合覆盖和路径覆盖发现错误的能力呈由弱至强的变化。\n', '单选题', 63);
INSERT INTO `question` VALUES (1460, '在实现阶段要完成的工作之一是单元测试，单元测试要根据在( )阶段中的规格说明进行 （） 。', '可行性研究与计划', '需求分析', '概要设计', '详细设计', 1, 'D', '在实现阶段要完成的工作之一是单元测试，单元测试要根据在(详细设计 )阶段中的规格说明进行 。在瀑布模型中，单元测试的计划应该在详细设计阶段制定，集成测试计划在概要设计阶段指定，确认测试在需求分析阶段制定，系统测试计划系统分析阶段制定。\n', '单选题', 63);
INSERT INTO `question` VALUES (1461, '十进制数512转换成二进制数是_______.', '111011101B', '1111111111B', '100000000B', '1000000000B', 1, 'D', '512 = 2^9= 1000000000B\n', '单选题', 63);
INSERT INTO `question` VALUES (1462, '目前PDF文件格式是一 种使用十分广泛的电子文档格式。在下列有关PDF文件格式的叙述中，错误的是_______.', '金山软件公司开发的WPS office能将所有处理的文件转换成PDF格式进行保存', '目前PDF文件格式已被批准为我国用于长期保存的电子文档格式的国家标准', 'PDF是由Microsoft公司开发的一种电子文档格式，目前并没有成为国际标准', '撰写、编辑、阅读和管理PDF文档，可以使用软件Adobe Acrobat', 1, 'C', 'PDF是由Adode Systems公司开发的,并成为国际标准。\n', '单选题', 63);
INSERT INTO `question` VALUES (1463, '（）属于移动互联网的特点。', '终端移动性', '业务与网络的弱关联性', '业务使用的私密性', '终端和网络的局限性', 2, 'ACD', '移动互联的特点有：\n（1）终端移动性；\n（2）业务使用的私密性；\n（3）终端和网络的局限性；\n', '多选题', 63);
INSERT INTO `question` VALUES (1464, 'Web2.0指的是一个利用Web的平台，由用户主导生成内容的互联网产品模式。（  ）属于Web2.0技术。', '微博', '相册', '百科全书(wiki)', '论坛', 2, 'ABC', 'Web2.0是相对于Web1.0而言。Web2.0是指一个利用Web的平台，由用户主导而生成的内容互联网产品模式，为了区别传统由网站雇员主导生成的内容而定义为第二代互联网，即Web2.0，是一个新的时代。\nWeb2.0去中心化、开放、共享为显著特征。\n1．用户分享。在Web2.0模式下，可以不受时间和地域的限制分享各种观点。用户可以得到自己需要的信息也可以发布自己的观点。\n2．信息聚合。信息在网络上不断积累，不会丢失。\n', '多选题', 63);
INSERT INTO `question` VALUES (1465, 'IP地址块59.67.159.125/11的子网掩码可写为______。', '', '', '', '', 4, ' 255.224.0.0', '由IP地址块59.67.159.125/11可知，网络号为前11位，则子网掩码为前11位全1、后21位为0，即为11111111 11100000 00000000 00000000，点分十进制为255.224.0.0。\n', '填空题', 63);
INSERT INTO `question` VALUES (1466, '神经网络研究属于______主义学派', '', '', '', '', 4, '连接', '神经网络研究属于连接主义学派\n', '填空题', 63);
INSERT INTO `question` VALUES (1467, '所谓的时间复杂度，是指最坏情况下估算算法执行时间的一个上界。', '', '', '', '', 3, '对', '时间复杂度：指执行算法所需要的计算工作量。通常，一个算法所用的时间包括编译时间和运行时间。度量方法是执行算法所需要的基本运算次数。', '判断题', 63);
INSERT INTO `question` VALUES (1468, '网页浏览、网上银行、淘宝购物等应用的客户端与服务器之间建立的TCP连接是“短连接”，数据传输完后即断开TCP连接，下次双方需要通信再重新建立连接。', '', '', '', '', 3, '对', '微信客户端与服务器之间建立的TCP连接是“长连接”，即通过3次握手建议连接之后开始数据传输，传输完了双方不会主动断开连接，之后不论那一方需要发送数据时，就继续通这个连接传输数据，TCP连接始终处于保持状态，这对于聊天之类通信频繁的场景非常适合。而网页浏览、网上银行、淘宝购物等应用的客户端与服务器之间建立的TCP连接是“短连接”，数据传输完后即断开TCP连接，下次双方需要通信再重新建立连接。', '判断题', 63);
INSERT INTO `question` VALUES (1469, '大数据赖以生存的土壤是_______。', '互联网', '物联网', '网络信息', '前沿技术', 1, 'A', '大数据赖以生存的土壤是互联网。\n', '单选题', 63);
