INSERT INTO `question` VALUES (1471, '在区块链上向某一个人转账时需要获取这个人的（  ）', '地址', '私钥', '账号', '姓名', 1, 'A', '在区块链中传输信息时，需要接收人的地址，然后使用接收的公钥进行加密。\n', '单选题', 63);
INSERT INTO `question` VALUES (1472, '当不知道数据对象有哪些类型时，可以使用_________使得同类数据对象与其他类型数据对象分离。', '分类', '聚类', '关联规则', '回归', 1, 'B', '当不知道数据对象有哪些类型时，可以使用聚类使得同类数据对象与其他类型数据对象分离。\n当知道那些类时用分类\n', '单选题', 63);
INSERT INTO `question` VALUES (1473, '在SOA中Web Service扮演着（）角色', '它提供了服务请求的调解机制', '它提供了一种信息框架的服务', '它支持不同技术间的可操作性', '它提供了描述服务接口的开放式的标准', 2, 'CD', 'Web 服务（Web Service）定义了一种松散的、粗粒度的分布式计算模式。Web服务的提供者利用WDSL描述Web 服务，Web 服务的使用者通过UDDI来发现服务，两者之间的通信采用SOAP协议。\n', '多选题', 63);
INSERT INTO `question` VALUES (1474, '云计算技术优势包括（  ）', '虚拟化', '分布式', '资源调度', '海量存储', 2, 'ABCD', '云计算技术优势虚拟化、分布式、并行计算、海量存储、桌面应用、资源调度、安全。\n', '多选题', 63);
INSERT INTO `question` VALUES (1475, '线性链表的运算主要包括（   ）', '在线性链表中包含指定元素的结点之前插入 个新元素', '复制线性链表', '线性链表的排序', '读栈顶元素', 2, 'ABC', '线性链表的运算主要包括：（1）在线性链表中包含指定元素的结点之前插入一个新元素。（2）在线性链表中删除包含指定元素的结点。（3) 将两个线性链表按要求合并成一个线性链表。（4）将一个线性链表按要求进行分解。（5）逆转线性链表。（6）复制线性链表。（7）线性链表的排序。（8）线性链表的查找。\n\n', '多选题', 63);
INSERT INTO `question` VALUES (1476, 'WWW资源（网页）有静态和动态的两大类，以下属于静态的资源的是（  ）', 'JS', 'MP3', 'GIF', 'JSP', 2, 'ABC', '﻿WWW资源（网页）有静态和动态的两大类:(1)静态资源通常指那些内容基本上不变的资源 ，例如类型为JPG、PNG、GIF、HTML、TXT、CSS、MP3、AVI之类的文件。(2)动态资源由HTML文档及其内嵌的ASP、PHP或JSP等编程语言编写的脚本程序所组成，发回给客户端的是Web服务器通执行脚本程序而实时和生成的。\n', '多选题', 63);
INSERT INTO `question` VALUES (1477, '根据传感器输出信号的性质不同分类,包括（  ）', '模拟量传感器', '数字量传感器', '开关量传感器', '智能传感器', 2, 'ABC', '根据传感器输出信号的性质不同分类：模拟量传感器（输出是模拟信号） 温度传感器AD590数字量传感器（输出是数字信号） 温度传感器DS18B20开关量传感器（输出两种状态） 热释电红外入侵传感器\n', '多选题', 63);
INSERT INTO `question` VALUES (1478, '以下有关对等网络的叙述，正确的有（  ）', '对等网络是分布式系统与计算机网络相结合的产物，是对等节点在物理网络之上形成的一种组网或网络形状。', '对等网络为了解决C/S模式所带来的问题而提出来的。', '对等网络中的参与者既是资源（服务和内容）提供者，又是资源的获取者。', '对等网络描述的是节点之间服务和被服务的关系，服务器为服务的提供者，客户端为服务的使用者。', 2, 'ABC', '选项A 正确。对等网络是分布式系统与计算机网络相结合的产物，是对等节点在物理网络之上形成的一种组网或网络形状。这种模式无需中央服务器的参与。选项B 正确。C/S模式的问题在于，一旦服务器出现问题，例如宕机或遭受攻击，客户端就无法使用相应的服务。对等网络为了解决C/S模式所带来的问题而提出来的。选项C 正确。对等网络中的参与者既是资源（服务和内容）提供者，又是资源的获取者，即在对等网络的角度并非像C/S模式下固定。选项D 错误。C/S模式描述的是节点之间服务和被服务的关系，服务器为服务的提供者，客户端为服务的使用者。\n', '多选题', 63);
INSERT INTO `question` VALUES (1479, '人工智能的远期目标是制造智能机器 ，近期目标是_____。', '', '', '', '', 4, '实现机器智能', '人工智能的远期目标是制造智能机器 ，近期目标是（实现机器智能）。\n', '填空题', 63);
