INSERT INTO `question` VALUES (1498, '传感器输出的信号一般为（）。', '物理量', '化学量', '生物量', '电量', 1, 'D', '传感器是一种检测装置（物理装置），能感受到被测量的信息，并能将检测到的信息按一定规律转换为电信号或其他所需形式的信息输出，以满足信息的传输、处理、存储、显示、记录和控制等要求。\n', '单选题', 63);
INSERT INTO `question` VALUES (1499, 'Hyperledger是Linux基金会发起的_______。', '公有链', '私有链', '联盟链', '中心化系统', 1, 'C', 'Hyperledger是联盟链\n', '单选题', 63);
INSERT INTO `question` VALUES (1500, '不同软件制作的丰富格式文本，它们使用的格式控制符(描述语言)等并不统一，其文件扩展名通常各不相同。目前已经成为ISO 32000国际标准,并被批准为我国用于长期保存的电子文档格式的国家标准的是______。', 'DOCX', 'HTML', 'RTF', 'PDF', 1, 'D', 'PDF文件格式已被批准为我国用于长期保存的电子文档格式的国家标准。\n', '单选题', 63);
INSERT INTO `question` VALUES (1501, '利用微波进行远距离通信需要依靠微波中继站进行接力通信，如果中继站安装在人造卫星上，此类微波通信称为“卫星通信”。\n下列有关卫星通信的叙述中，错误的是_________。', '通信卫星的运行轨道有中低轨道和同步定点轨道两种,利用同步卫星通信已成为主要方式', '从理论上说, 24颗卫星就可以覆盖地球的几乎全部面积，可以进行24小时的全天候通信', '卫星通信的主要特点是通信距离远、频带宽、容量大，信号受到的干扰也较小，通信比较稳定', '卫星通信的另一种重要应用是导航和定位，可为用户提供实时的全天候、全球性的导航定位服务', 1, 'B', '从理论上说，3颗卫星就可以要盖地球的几乎全部面积，可以进行24小时的全天候通信。\n', '单选题', 63);
INSERT INTO `question` VALUES (1502, '网格计算是利用（）技术，把分散在不同地理位置的计算机组成一台虚拟超级计算机。', '对等网', '因特网', '广域网', '无线网', 1, 'B', '网格计算（Grid Computing）是指利用互联网把地理上广泛分布的各种资源（例如，计算资源、存储资源、带宽资源、软件资源、数据资源、信息资源、知识资源等），连成一个逻辑整体，就像一台超级计算机一样，为用户提供一体化信息和应用服务（如计算、存储、访问等）\n', '单选题', 63);
INSERT INTO `question` VALUES (1503, '汉字的字形码存放在( )。', '汉字库文件中', '键盘管理程序中', '汉字系统启动程序中', '显示管理程序中', 1, 'A', '汉字的字形码存放在汉字库文件中。\n', '单选题', 63);
INSERT INTO `question` VALUES (1504, '字符集及其编码是计算机中表示、存储、处理和交换文本信息的基础，但由于历史原因，目前不同系统或应用中使用的字符编码方案并不统一。下\n列有关汉字编码的叙述中， 正确的有（  ）。', '在我国最早颁布的汉字编码字符集GB 2312仅包含6000多个汉字,其中没有繁体汉字', '无论是GB 2312、 GBK, 还是GB 18030, 其中西文字符均为单字节编码，汉字均为双字节编码', '目前在互联网的许多应用中采用UTF- -8编码，该编码方案采用可变长度字符编码', '目前在Windows操作系统中常 使用UTF- -16编码,在该编码方案中汉字编码使用2个字节表示', 2, 'ACD', 'GB 2312的所有字符在计算机中内部都采用2个字节（16个二进位）来表示，每个字字最高位均规定为1。\n', '多选题', 63);
INSERT INTO `question` VALUES (1505, '下列关于数据库系统的叙述，正确的有（  ）', '数据库是指按一定数据模型组织并长期存放外存上的、可共享的相关数据集合', '数据逻辑结构和物理存储改变不影响用户的应用程序', '数据库系统的支持环境不包括操作系统', '对数据库的一切操作都是通过DBMS进行的', 2, 'ABD', '数据库(DB)是一个长期存储在计算机内的、有组织的、可共享的、统一管理的大量数据的集合。外存可以长久保存。选项A 正确。数据库的二级映像：（1）外模式/模式映像：保证逻辑独立性。用户的应用程序和数据库中的逻辑结构是相互独立的,当数据的逻辑结构发生改变时应用不需要改变. 逻辑独立性存在于外模式和内模式之间。数据的逻辑独立性是指数据与程序的逻辑独立性。（2）模式/内模式映像：保证物理独立性。用户的应用程序与存储在磁盘上的数据库中的数据是相互独立的。物理存储改变时，用户的应用程序不需要改变。 选项B 正确。数据库系统（DBS）由数据库（DB）、数据库管理系统DBMS（及其应用开发工具）、应用程序、数据库管理员（DBA）以及用户组成。DBMS是系统软件。选项C 错误。操作数据库里的数据需要通过DBMS来完成。选项D 正确。\n', '多选题', 63);
INSERT INTO `question` VALUES (1506, '“软件危机”产生的主要原因有（ ）', '随着社会和生产的发展，软件无法存储和处理海量数据', '随着问题的复杂度增加，人们开发软件的效率下降', '对软件需求分析的重要性认识不够', '软件开发过程难于进行质量管理和进度控制', 2, 'BCD', '软件危机泛指在计算机软件的开发和维护过程中遇到的一系列严重的问题，集中表现在成本，质量，生产效率等几个方面。导致软件危机的主要原因有：软件规模越来越大；软件复杂度越来越大；软件需求不明确，容易造成无用和返工；软件开发缺乏正确的理论指导等。\n', '多选题', 63);
INSERT INTO `question` VALUES (1507, 'PROLOG是一种通用高级计算机程序语言，是第一个声明式系内函数式程序设计语言,长期以来垄断人工智能领域的应用。', '', '', '', '', 3, '错', 'Prolog一般指逻辑编程语言。 Prolog(Programming in logic)是一种面向演绎推理的逻辑型程序设计语言，最早于1972年由柯尔麦伦纳(Colmeraner)及其研究小组在法国马赛大学提出。LISP是一种通用高级计算机程序语言，长期以来垄断人工智能领域的应用。LISP作为因应人工智能而设计的语言，是第一个声明式系内函数式程序设计语言，有别于命令式系内过程式的C、Fortran和面向对象的Java、C#等结构化程序设计语言。', '判断题', 63);
INSERT INTO `question` VALUES (1508, '数字签名就是实现网上交易安全的核心技术之一,它可以保证信息传输的保密性、数据交换的完整性、发送信息的不可否认性、交易者身份的确定性等。', '', '', '', '', 3, '对', '为了确保数据传输的安全性，不得不采取一系列的安全技术，如加密技术、数字签名、身份认证、密钥管理、防火墙、安全协议等。其中数字签名就是实现网上交易安全的核心技术之一，它可以保证信息传输的保密性、数据交换的完整性、发送信息的不可否认性、交易者身份的确定性等。', '判断题', 63);
INSERT INTO `question` VALUES (1509, '开源软件指在软件开发行的时候可以附上部分的源代码，并在协议规定下用户可以修改和再发布。', '', '', '', '', 3, '错', '开源软件指在软件开发行的时候必须附上完整的源代码，并在协议规定下用户可以修改和再发布。', '判断题', 63);
INSERT INTO `question` VALUES (1510, '信息技术指的是用来扩展人们信息器官功能、协助人们更有效地进行信息的一类技术。例如，扩展了人们的效应器官功能的是______。', '感测与识别技术', '计算与存储技术', '通信技术', '控制与显示技术', 1, 'D', '信息技术主要是指信息处理技术，包括扩展人的感觉器官(眼,耳等五官)功能的是感测与识别技术,扩展神经系统功能的通信技术，扩展大脑功能的计算与存储技术，扩展效应器官(手，脚等)功能的控制与显示技术。\n', '单选题', 63);
INSERT INTO `question` VALUES (1511, '机器字长为n位的二进制数可以用补码来表示（  ）个不同的有符号定点小数。', '2^n', '2^(n-1)', '2^ n-1', '2^(n-1)+1', 1, 'A', '补码表示定点小数，范围是： [-1,(1-2^-(n-1))]，这个范围一共有2n个数。0.0是小数\n', '单选题', 63);
INSERT INTO `question` VALUES (1512, '私有链的特点有（  ）', '交易速度快', '隐私性更强', '交易成本大幅降低，甚至为零', '需要耗费时间', 2, 'ABC', '1、交易速度非常之快一个私有链的交易速度可以比任何其他的区块链都快，甚至接近了并不是一个区块链的常规数据库的速度。这是因为就算少量的节点也都具有很高的信任度，并不需要每个节点来验证一个交易。<br/>2、给隐私更好的保障私有链使得在那个区块链上的数据隐私政策像在另一个数据库中似的完全一致;不用处理访问权限和使用所有的老办法，但至少说，这个数据不会公开地被拥有网络连接的任何人获得。<br/>3、交易成本大幅降低甚至为零私有链上可以进行完全免费或者至少说是非常廉价的交易。如果一个实体机构控制和处理所有的交易，那么他们就不再需要为工作而收取费用。然而，即使交易的处理是由多个实体机构完成的，例如竞争性银行，进一步举例来说，因为同样的原因，它们可以如此之快的处理交易，所以费用仍然是非常小的;这并不需要节点之间的完全协议，所以很少的节点需要为任何一个交易而工作。<br/>4、有助于保护其基本的产品不被破坏正是这一点使得银行等金融机构能在目前的环境中欣然接受私有链，银行和政府在看管他们的产品上拥有既得利益，用于跨国贸易的国家法定货币仍然是有价值的。由于公有链的直接应用是保护像比特币这样新型的非国家性质的货币，对核心利润流或组织构成了破坏性的威胁，这些实体机构应该会不惜一切代价去避免损害。\n', '多选题', 63);
INSERT INTO `question` VALUES (1513, '数值、文字、图像、声音等不同的信息在计算机中的表示方法是不同的。下列相关叙述中，正确的有_______。', '数值为负的整数在计算 机中通常采用“反码”的方法进行表示', '文字由一 系列“字符”构成,每个字符通常使用1-4个字节的二进制编码表示', '黑白图像在数据库时，每个像素一般采用8位二进制无符号整数表示', '声音信息在数字化时， 每个取样的样本一般使用8位、 12位、14位或16位二进制数表示', 2, 'BD', '数值为负的整数在计算 机中通常采用“补码”的方法进行表示; 选项A错误 。黑白图像在数据库时，每个像素一般采用1位二进制无符号整数表示.选项C 错误 。\n', '多选题', 63);
INSERT INTO `question` VALUES (1514, '有关在系统和软件设计中通常会采取一些措施的描述，正确的是（   ）', '对通信双方的身份和所传信息的真伪进行鉴别，防止抵赖', '保护数据秘密，未经解密不会显露其内容，防止窃听', '通信双方确认发送和接收了信息，防止假冒', '保护数据不被修改，防止篡改', 2, 'BD', '网络安全的技术措施（黄色为五大要素）（1）真实性鉴别（真实性）∶对通信双方的身份和所传输的信息的真伪能准确地进行鉴别，—身份认证，可以防假冒。（2）访问控制∶控制用户对信息等资源的访问权限，防止未经授权使用资源，防盗窃。（3）数据加密（保密性）∶保护数据秘密，未经授权其内容不会显露，防止窃听。 INSERT INTO `question` VALUES (1515, '下列关于存储器的描述中，正确的有（  ）。', '所有的数据和指令均以二进制数的形式存放在存储器中', '存储器可分为内存和外存', '内存由半导体构成，速度快但单位容量的成本高，容量较小', '外存可以由硬盘、光盘等非半导体介质构成，存储密度低、单位容量的成本低', 2, 'ABC', '外存储器主要有磁存储器、半导体集成电路存储器、光存储器等。磁存储器是计算机的主要外存储器，现在大多用高密度的磁带和磁盘。\n', '多选题', 63);
INSERT INTO `question` VALUES (1516, '有关计算机性能指标的时钟主频，下面的描述中，正确的有（  ）。', '时钟主频是指CPU的时钟频率', '时钟主频的高低一定程度上决定了计算机速度的高低', '主频以MHz为单位', '一般来说，主频越高，速度越快', 2, 'ABD', '计算机主频以 GHz 为单位。选项C错误。其他选项都正确。\n', '多选题', 63);
INSERT INTO `question` VALUES (1517, '数字音频是一种使用__________表示的的按时间顺序组织的串行比特流。', '', '', '', '', 4, '二进制', '数字音频是一种使用二进制表示的的按时间顺序组织的串行比特流。\n', '填空题', 63);
INSERT INTO `question` VALUES (1518, '显示卡（显卡）是主机与显示之间的“桥梁”，它负责将CPU需要输出的图形和文字转换成显示器可以接受的________图像（称为图形绘制或绘图），然后 以规定的频率一帧一帧地送至显示器，在显示器屏幕上呈现出用户可见的图像。', '', '', '', '', 4, ' 位图', '显示卡（显卡）是主机与显示之间的“桥梁”，它负责将CPU需要输出的图形和文字转换成显示器可以接受的位图图像（称为图形绘制或绘图），然后 以规定的频率一帧一帧地送至显示器，在显示器屏幕上呈现出用户可见的图像。\n', '填空题', 63);
INSERT INTO `question` VALUES (1519, 'ART是一种Java虚拟机，使用_______语言编写的App源程序经编译后必须转换成dex格式才能在ART虚拟机上执行。', '', '', '', '', 4, 'Java', 'ART是一种Java虚拟机，使用Java语言编写的App源程序经编译后必须转换成dex格式才能在ART虚拟机上执行。\n', '填空题', 63);
INSERT INTO `question` VALUES (1520, '目前300DPI是人眼分辨文本与图形边缘是否有锯齿的临界点，但通常必须达到400DPI以上的打印效果才能基本令人满意。', '', '', '', '', 3, '错', '目前300DPI是人眼分辨文本与图形边缘是否有锯齿的临界点，再考虑一些因素，因此通常需要达到360DPI以上的打印效果才能基本令人满意。', '判断题', 63);
INSERT INTO `question` VALUES (1521, '同一个算法，实现语言级别越低，执行效率越高。', '', '', '', '', 3, '对', '语言的级别越高，编译器背着我们干的事就越多，所以效率就越低。尤其在“同一个算法”的限制下，这句话在绝大多数情况下是成立的', '判断题', 63);
INSERT INTO `question` VALUES (1522, '在Windows 10上，驱动程序包中的每一个文件都有一个对应的哈希值，可以将这个哈希值称为“指纹”，且包仓 数字签名中，具体而言驱动程序包中的所有指纹存储在扩展名为（）的文件中，将该文件称为“安全目录文件夹”。', '.sys', '.inf', '.cat', '.dll', 1, 'C', '在Windows 10上，驱动程序包中的每一个文件都有一个对应的哈希值，可以将这个哈希值称为“指纹”，且包仓 数字签名中，具体而言驱动程序包中的所有指纹存储在扩展名为.cat的文件中，将该文件称为“安全目录文件夹”。<br/>\nINF (文件) 安装信息是驱动程序包中的文本文件，其中包含设备安装组件在设备上安装驱动程序包时使用的所有信息。<br/> \n sys文件是驱动程序的可执行代码 \n', '单选题', 63);
INSERT INTO `question` VALUES (1523, '在Winows 10上，______是将文件和文件夹移动或复制到一个系统的固定位置进行共享。', '', '', '', '', 4, '公用文件夹共享', '在Winows 10上，公用文件夹共享 是指共享位于安装Windows 10的磁盘分区中名为“公用”的文件夹中的内容。\n', '填空题', 63);
INSERT INTO `question` VALUES (1524, '在MySQL中，实现实现返回字符串\'smartfox\'的前5个字符。SQL语句如下：\nmysql> SELECT ____________ (\'smartfox\',5);', '', '', '', '', 4, 'LEFT', 'LEFT(s,n)函数用于返回字符串s的前n个字符。\n', '填空题', 63);
INSERT INTO `question` VALUES (1525, '某个IP地址的子网掩码为255.255.255.192， 该掩码又可以写为________。', '', '', '', '', 4, ' /26', '﻿﻿某个IP地址的子网掩码为255.255.255.192， 该掩码又可以写为/26。', '填空题', 63);
INSERT INTO `question` VALUES (1526, '对8位二进制数最高位取反，其他位不变以下操作中错误的是_______。', '用0减去该数', '将它加上10000000B', '将它和10000000B异或', '将它和01111111B同或', 1, 'A', '选项A中。 假如八位二进制是 00000100，00000000-00000100 = 11111100。这时候发现除了最高位，其他他也有可能会取反。所以错误。选项B。假如八位二进制是 00000100，10000000+00000100 = 10000100。最高位取反了，其他位并没有变。选项C。假如八位二进制是00000100，10000000 ⊕00000100 = 10000100。最高位取反了，其他位并没有变。选项D。假如八位二进制是00000100，01111111 ⊙00000100 = 10000100。最高全取反了，其他位并没有变。\n', '单选题', 63);
INSERT INTO `question` VALUES (1527, '通过编译程序将高级语言翻译为机器语言,首先要进行的工作是_______。', '词法分析', '语法分析', '语义分析', '代码优化', 1, 'A', '编译程序的工作过程可以分为词法分析、语法分析、语义分析、中间代码生成、代码优化和目标代码生成等6个阶段。\n', '单选题', 63);
INSERT INTO `question` VALUES (1528, '设n1,n2为二叉树的两个结点中序遍历时n1在n2的前面以下说法正确的是____.', 'n1必然在n2的左方', 'n1必然在n2的右方', 'n1必然是n2的祖先结点', 'n1必然是n2的子孙结点', 1, 'A', '中序遍历：左 -根-右的顺序，n1在n2的前面,则说明n2可以是根，也可以是n2是右。n1必然在n2的左方。\n', '单选题', 63);
INSERT INTO `question` VALUES (1529, '在采用多级目录结构的系统中，经常采用 ___方法来提高检索文件的速度。', '避免重名', '限制存取权限', '相对路径', '文件分类存放', 1, 'C', '在采用多级目录结构的系统中，经常采用 相对路径方法来提高检索文件的速度。\n', '单选题', 63);
INSERT INTO `question` VALUES (1530, '在绘制数据流图（DFD)时，（  ）。', '每个加工至少有一个输入数据流，可以没有输出数据流', '不允许一个加工有多条数据流流向另一个加工', '不允许一个加工有两个相同的输出数据流流向两个不同的加工', '必须保持父图与子图平衡', 1, 'D', '数据流图设计注意事项：①自外向内，自顶向下，逐层细化，完善求精；②保持父图与子图的平衡(绘制分层数据流图，应该严格遵循父子图平衡原则)；③保持数据守恒；④加工细节隐藏；⑤简化加工间的关系；⑥均匀分解；⑦适当取名(每个数据流、加工、数据存储和外部实体命名)，避免空洞的名字；⑧表现的是数据流而不是控制流（在分层数据流图并没有强调过需要表示出控制流）；⑧每个加工必须既有输入数据流，又有输出数据流，一个加工不适合有过多的数据流。\n', '单选题', 63);
INSERT INTO `question` VALUES (1531, '小张买了一个内存条，具体内存条如下，时钟频率是 200MHz,则内存条的带宽大约是（）。<br>\n***************************<br>\n*---------------------DDR4*<br>\n*-----------------------------*<br>\n*********** ***************<br>', '21.3GB/s', '25.6GB/s', '21.3Gb/s', '25.6Gb/s', 1, 'B', '内存带宽的计算(★★★☆☆)正常位宽默认是64<br>公式一：内存带宽=工作频率X位宽/8。<br>公式二：内存带宽 = 时钟频率*2^(代数)* 位宽/8。<br>注意DDR 代数就是1，DDR 2代数 2，依次类推。本题使用公式2：内存带宽 = 200MHz *2^(4) *64/8= 3200 * 8 / 1000 = 25600/1000 = 25.6GB/s\n', '单选题', 63);
INSERT INTO `question` VALUES (1532, '对于不同数制之间关系的描述,正确的描述为 （）', '任意的二进制有限小数转换成十进制后,必定也是十进制有限小数', '任意的八进制有限小数转换成二进制数后,未必还是二进制有限小数', '任意的十六进制有限小数转换成十进制数后,不一定还是十进制有限小数', '任意的十进制有限小数转换成八进制数,必然还是八进制有限小数', 1, 'A', '任意的二进制有限小数转换成十进制后,必定也是十进制有限小数，按权值展开相加即可，得到的必定是有限十进制小数。选项A 正确。任意的八进制有限小数转换成二进制数后,一定还是二进制有限小数，八进制转二进制是1位8进制用3位二进制表示。选项B 错误。任意的十六进制有限小数转换成十进制数后,一定还是十进制有限小数，按权值展开相加即可，选项C 错误。任意的十进制有限小数转换成八进制，整数可以，小数不一定可以转换有限小数。选项D 错误 \n', '单选题', 63);
INSERT INTO `question` VALUES (1533, '移动互联网与互联网最大的区别是引入了移动通信的（  ）', '移动性', '便携性', '定位性', '社交性', 2, 'ABCD', '移动互联网的特征：(1)交互性：用户可以随身携带和随时使用移动终端，在移动状态下接人和使用移动互联网应用服务。(2)便携性：移动终端小巧轻便、可随身携带两个特点。(3)隐私性：由于移动性和便携性的特点，移动互联网的信息保护程度较高。(4)定位性： 远程签到、位置共享，打车定位、导航等(5)娱乐性：头条、抖音、皮皮虾、网易云音乐、当当云阅读等。(6)局限性：受到了来自网络能力和终端硬件能力的限制(7)强关联性：通信带宽不足时影响在线视频、视频通话，移动网游等(8)身份统一性：指移动互联用户自然身份、社会身份、交易身份、支付身份通过移动互联网平台得以统\n', '多选题', 63);
INSERT INTO `question` VALUES (1534, '下面描述中，属于软件危机表现的有( )', '软件开发生产率低', '软件过程不规范', '软件质量难以控制', '软件成本不断提高', 2, 'ACD', '软件危机泛指在计算机软件的开发和维护过程中所遇到的一系列严重问题。软件危机主要表现:软件开发进度难以预测;软件开发成本难以控制;用户对产品功能难以满足;软件产品质量无法保证;软件产品难以维护;软件缺少适当的文档资料。\n', '多选题', 63);
INSERT INTO `question` VALUES (1535, '下列有数据、数据元素、数据项之间说法，错误的有（  ）', '数据是数据元素的基本单位', '数据元素是数据项中不可分割的最小标识单位', '数据可由若干数据元素构成', '数据项可由若干个数据元素构成', 2, 'ABD', '数据元素是数据的基本单位。选项A错误 。数据可由若干数据元素构成。选项C 正确。若干个数据项构成一个数据元素，数据项是构成数据元素的最小单位。选项BD错误。\n', '多选题', 63);
INSERT INTO `question` VALUES (1536, '关于ZigBee技术特点,下列说法中正确的有（  ）', '低功耗,低成本', '时延短,网络容量大', '安全,可靠', '短距离,速率高', 2, 'ABC', 'ZigBee（紫蜂） 技术是一种近距离、低复杂度、低功耗、低速率、低成本的双向无线通信技术。广泛应用于智能家居、楼宇自动化、工业自动化等ZigBee 网络的主要特点是：功耗低、成本低、时延小、网络容量大（一个ZigBee网络最多可以容纳254个从设备和1个主设备， 一个区域内最多可以同时存在100个ZigBee网络，而且组网灵活）、可靠性高、安全，主要适用于自动控制和远程控制领域，可以嵌入各种设备。\n', '多选题', 63);
INSERT INTO `question` VALUES (1537, '国产华为智能手机Mate10、P20和荣耀10使用的麒麟970芯片中，也包含了1个______________NPU(寒武纪1A)，浮点运算性能可达1.92TFLOPS，相比使用4个Cortex-A73CPU处理两样的人工智能任务，性能可提高25倍。', '', '', '', '', 4, ' 神经网络处理器', '无', '填空题', 63);
INSERT INTO `question` VALUES (1546, '顺序存储方法是把逻辑上相邻的结点存储在物理位置上也相邻的_________的存储单元里，由此得到的存储结构称为顺序存储结构。', '', '', '', '', 4, ' 连续', '顺序存储方法是把逻辑上相邻的结点存储在物理位置上也相邻的连续的存储单元里，由此得到的存储结构称为顺序存储结构。\n', '填空题', 66);
INSERT INTO `question` VALUES (1547, '在线性表上进行查找的方法有顺序查找、_________和分块查找。', '', '', '', '', 4, ' 折半查找', '在线性表上进行查找的方法有顺序查找、拆半查找(二分查找)和分块查找。\n', '填空题', 66);
INSERT INTO `question` VALUES (1548, '有关算法的特点叙述，正确的有（  ）', '算法的每一条指令必须有确切的定义，无二义性', '算法必须能在执行有限个步骤之后终止', '算法中的描述在计算机中能执行', '必须要有输入和输出', 2, 'ABC', '算法的必要满足条件（有些教材称为五大特性，最后一条拆为无输入和至少一输出）：①、确定性：算法的每一条指令必须有确切的定义，无二义性。②、有穷性：是指算法必须在有限的时间内做完，即算法必须能在执行有限个步骤之后终止。③、可行性：算法中的描述在计算机中能执行，且在有穷时间内完成。④、至少有一个（1个或多个）输出，但是可以没有（0个或多个）输入\n', '多选题', 66);
INSERT INTO `question` VALUES (1549, '计算机的病毒特点包括（  ）', '传染性', '隐蔽性', '潜伏性', '破坏性', 2, 'ABCD', '计算机病毒的特点∶①破坏性、②隐蔽性、③传染性、④潜伏性、⑤寄生性。\n', '多选题', 66);
INSERT INTO `question` VALUES (1550, '（   ）是利用互联网上的计算机的中央处理器的闲置处理能力来解决大型计算问题的一种计算科学。', '分布式计算', '并行计算', '网络计算', '串行计算', 1, 'A', '分布式计算是利用互联网上的计算机的中央处理器的闲置处理能力来解决大型计算问题的一种计算科学。\n', '单选题', 66);
INSERT INTO `question` VALUES (1551, '（   ） 是一个能够让用户轻松架构和使用的分布式计算的平台，其架构中主要包括MapRedue和HDFS。', 'Hadoop', '虚拟化', 'Azure', 'GFS', 1, 'A', 'Hadoop是一个能够让用户轻松架构和使用的分布式计算的平台，其架构中主要包括MapRedue和HDFS。\n', '单选题', 66);
INSERT INTO `question` VALUES (1552, '编译程序只能将高级语言转换到机器语言。', '', '', '', '', 3, '错', '编译程序∶将程序从高级语言转换到机器语言或汇编语言的翻译程序', '判断题', 66);
INSERT INTO `question` VALUES (1553, 'CPU工作时，它所执行的的指令及处理的数据都是从主存取出的，产生的结果一般也主存中，当需要数据不存在主存中，就直接去外存中取。', '', '', '', '', 3, '错', 'CPU工作时，它所执行的的指令及处理的数据都是从主存取出的，产生的结果一般也主存中，当需要数据不存在主存中，需要先调入内存，再由CPU去访问内存。', '判断题', 66);
